INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12458, "", "バスタオル", "バスタオル", "これ、何と言う？", 12523, 0, 5, 12479, 5, 12496, 12473, 5, 5135, 5, "大きなサイズのタオルで、文字通り風呂上りに身体を乾かすのに使う", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12394, "", "ぼたんなべ", "ぼたんなべ", "これ、何と言う？", 12409, 0, 4, 12435, 4, 12412, 12383, 5, 5136, 4, "イノシシの肉を使った鍋料理の、「猪鍋」以外の呼び方。縮れた肉がとある花に似ていることから", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12459, "", "ンギスカン", "ジンギスカン", "これ、何と言う？", 12531, 0, 5, 12473, 5, 12531, 12462, 5, 5137, 5, "羊肉を使った料理で非常にポピュラーなもの。特殊な形の鉄板で、野菜と一緒に羊肉を焼く", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12483, "", "メゾネット", "メゾネット式", "これ、何と言う？", 12488, 0, 5, 12493, 5, 12513, 12478, 5, 5138, 5, "一般に、マンションやアパートでありながら、内部に階段があり2階構造になっている物件のこと", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12502, "リバーシブル", "リバシブル", "リバーシブル", "これ、何と言う？", 12523, 0, 5, 12471, 5, 12522, 12496, 5, 5139, 5, "裏返しでも着れるように作られている服のこと", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "アイランド", "アイランドキッチン", "これ、何と言う？", 12489, 0, 5, 12521, 5, 12450, 12452, 5, 5140, 5, "普通の壁に向いた形のキッチンと異なり、島のように独立した形のキッチン", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "海のミルク", "海のミルク", "これ、何と言う？", 12463, 0, 4, 12511, 6, 28023, 12398, 5, 5141, 5, "「牡蠣」の別名。乳白色をしており、また栄養バランスの良さがとある飲み物を思わせることから", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12421, "", "しんきゅう", "しんきゅう師", "これ、何と言う？", 12358, 0, 4, 12365, 4, 12375, 12435, 5, 5142, 4, "「はり師」と「きゅう師」二つの資格を有する人の職業、何という？", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "タコライス", "タコライス", "これ、何と言う？", 12473, 0, 5, 12521, 5, 12479, 12467, 5, 5143, 5, "「タコス」の具材を、生地で挟むのではなくご飯の上に乗せる形でアレンジした料理", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "ワーキング", "ワーキングホリデー", "これ、何と言う？", 12464, 0, 5, 12461, 5, 12527, 12540, 5, 5144, 5, "若者が、他国で働いて資金を稼ぎながら、そこの文化に触れ長期休暇を過ごすという、公の制度", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12515, "", "チンジャオ", "青椒肉絲＝チンジャオロース", "これ、何と読む？", 12458, 0, 5, 12472, 5, 12481, 12531, 5, 5145, 5, "", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "シーズニング", "シズニング", "シーズニング", "これ、何と言う？", 12464, 0, 5, 12491, 5, 12471, 12474, 5, 5146, 5, "鉄のフライパンが錆びないように行う「空焼き」と「油ならし」の2工程を合わせてこう呼ぶ", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "ホイコーロ", "回鍋肉＝ホイコーロ", "これ、何と読む？", 12525, 0, 5, 12467, 5, 12507, 12452, 5, 5147, 5, "", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12493, "", "しらがネギ", "しらがネギ", "これ、何と言う？", 12462, 0, 4, 12364, 4, 12375, 12425, 5, 5148, 5, "ネギの白い部分を、まるで髪の毛のようにごく細くせん切りにしたもの。和食の飾りに使われる", 4, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "モロヘイヤ", "モロヘイヤ", "これ、何と言う？", 12516, 0, 5, 12504, 5, 12514, 12525, 5, 5149, 5, "特別に栄養価が高いことから「野菜の王様」とも呼ばれる緑色の葉野菜", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12472, "", "スムージー", "スムージー", "これ、何と言う？", 12540, 0, 5, 12540, 5, 12473, 12512, 5, 5150, 5, "凍らせた果物や野菜などをミキサーにかけて作る飲み物で、朝食に好まれる", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12451, "", "ルーティン", "ナイトルーティン", "これ、何と言う？", 12531, 0, 5, 12486, 5, 12523, 12540, 5, 5151, 5, "毎日の夜に行う、自分ならではの習慣のこと。近年ではタレントが動画に撮って発信するのが人気", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "バンバンジ", "棒棒鶏＝バンバンジー", "これ、何と読む？", 12472, 0, 5, 12496, 5, 12496, 12531, 5, 5152, 5, "蒸した鶏肉をごまだれで味付けた中華料理。キュウリを添えることが多い", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12354, "", "いそべあげ", "いそべあげ", "これ、何と言う？", 12370, 0, 4, 12409, 4, 12356, 12381, 5, 5153, 4, "天ぷらの一種。「青のり」を混ぜた衣で、ちくわなどの練り物を揚げた食べ物", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12426, "", "おにぎりざ", "おにぎりざ", "このパネルの「プリコネ読み」は？", 12374, 5154, 4, 12366, 4, 12362, 12395, 5, 5154, 4, "", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12522, "", "ダージリン", "ダージリン", "これ、何と言う？", 12531, 0, 5, 12472, 5, 12480, 12540, 5, 5155, 5, "非常にポピュラーな紅茶の種類。「ウバ」「キーマン」に並び、「世界三大銘茶」と評される", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12524, "アールグレイ", "アルグレイ", "アールグレイ", "これ、何と言う？", 12452, 0, 5, 12464, 5, 12450, 12523, 5, 5156, 5, "ポピュラーな紅茶の一種で、お菓子作りの材料にも好まれる。伯爵という意味の言葉が含まれる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12515, "", "ジンジャー", "ジンジャーエール", "これ、何と言う？", 12540, 0, 5, 12472, 5, 12472, 12531, 5, 5157, 5, "非常にポピュラーな、炭酸飲料の一種。ショウガの風味が特徴的", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "エンゲル係", "エンゲル係数", "これ、何と言う？", 20418, 0, 5, 12466, 5, 12456, 12531, 5, 5158, 6, "家庭の支出額のうち、「食費」が占める割合のこと", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12383, "", "プリンたい", "プリンたい", "これ、何と言う？", 12356, 0, 5, 12531, 5, 12503, 12522, 5, 5159, 4, "一般にビールやレバー、エビ、牡蠣などに多く含まれる成分。あまりとり過ぎると痛風の原因に", 5, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12358, "", "きぬどうふ", "きぬどうふ", "これ、何と言う？", 12405, 0, 4, 12393, 4, 12365, 12396, 5, 5160, 4, "豆腐の種類として特によく食べられる2種類。1つは「木綿豆腐」で、もう一つは？", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12457, "インフォームド・コンセント", "インフォム", "インフォームド・コンセント", "これ、何と言う？", 12512, 0, 5, 12501, 5, 12452, 12531, 5, 5161, 5, "患者が医師から十分な説明を受け、合意のうえで医療行為を受けること", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12419, "", "だんしゃり", "だんしゃり", "これ、何と言う？", 12426, 0, 4, 12375, 4, 12384, 12435, 5, 5162, 4, "元は「ヨガ」から生まれた習慣で、不要なものを捨て、物に執着せずに生きること", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12405, "", "スギかふん", "スギかふん", "これ、何と言う？", 12435, 0, 5, 12363, 5, 12473, 12462, 5, 5163, 4, "花粉症の原因として最も多いのは、どんな種類の花粉？", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12463, "", "フレックス", "フレックスタイム制", "これ、何と言う？", 12473, 0, 5, 12483, 5, 12501, 12524, 5, 5164, 5, "固定の時間帯で働く従来の働き方とは違い、始業と終業の時間を自由に決められる労働制度", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12363, "", "きゅうかん", "きゅうかん日", "これ、何と言う？", 12435, 0, 4, 12358, 4, 12365, 12421, 5, 5165, 4, "アルコールをあまり毎日飲み過ぎず、肝臓を休める日を適度に設けることを推奨する言葉", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "マヨネーズ", "マヨネーズ", "これ、何と言う？", 12474, 0, 5, 12493, 5, 12510, 12520, 5, 5166, 5, "卵・酢・油などから作られ、サラダなどにかけることが多い、定番の調味料", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12483, "", "ケチャップ", "ケチャップ", "これ、何と言う？", 12503, 0, 5, 12515, 5, 12465, 12481, 5, 5167, 5, "主にトマトなどから作られる、定番の赤い調味料。オムライスやフライドポテトなどによく合う", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12522, "", "マーガリン", "マーガリン", "これ、何と言う？", 12531, 0, 5, 12460, 5, 12510, 12540, 5, 5168, 5, "トーストなどに塗るのが定番の加工食品。バターの代替品として使われることも", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 37218, "砂糖・塩・酢・醤油・味噌", "砂糖塩酢醤", "砂糖・塩・酢・醤油・味噌", "これ、何と言う？", 37284, 0, 6, 22633, 6, 30722, 31958, 5, 5169, 6, "料理の「さしすせそ」、順番に何と言う？", 6, 3, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12414, "", "ごこくまい", "ごこくまい", "これ、何と言う？", 12356, 0, 4, 12367, 4, 12372, 12371, 5, 5170, 4, "健康食品として知られるお米。米のほかに麦や粟など、合計5種類の穀物が合わさっている", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12501, "", "ビュッフェ", "ビュッフェ", "これ、何と言う？", 12455, 0, 5, 12483, 5, 12499, 12517, 5, 5171, 5, "ホテルなどで多い、セルフサービス形式での食事。「バイキング」と混同されることが少なくない", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12388, "", "たかのつめ", "たかのつめ", "これ、何と言う？", 12417, 0, 4, 12398, 4, 12383, 12363, 5, 5172, 4, "トウガラシの代表的な品種。名前は、とある鳥のかぎづめに似ていることから", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12460, "", "タラバガニ", "タラバガニ", "これ、何と言う？", 12491, 0, 5, 12496, 5, 12479, 12521, 5, 5173, 5, "高級食材としてよく知られるカニ。魚のタラが獲れる場所に生息していることが由来", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12456, "", "サクラエビ", "サクラエビ", "これ、何と言う？", 12499, 0, 5, 12521, 5, 12469, 12463, 5, 5174, 5, "食用として、かき揚げなどで好まれる小さなエビ。透き通ったピンク色の身体をしている", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12421, "さんきゅっぱ", "さんきゅぱ", "さんきゅっぱ", "これ、何と言う？", 12401, 0, 4, 12365, 4, 12373, 12435, 5, 5175, 4, "売り物によく設定される「398円」という値段を読みやすくした読み方", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12375, "", "かいばしら", "かいばしら", "これ、何と言う？", 12425, 0, 4, 12400, 4, 12363, 12356, 5, 5176, 4, "ホタテなどの二枚貝で、円柱の形をした大きな可食部の通称", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12473, "", "ファースト", "ファーストバイト", "これ、何と言う？", 12488, 0, 5, 12540, 5, 12501, 12449, 5, 5177, 5, "結婚式で、ケーキ入刀のあとに新郎新婦がお互いにケーキを食べさせあう演出", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12362, "", "いろなおし", "おいろなおし", "これ、何と言う？", 12375, 0, 4, 12394, 4, 12356, 12429, 5, 5178, 4, "結婚披露宴の途中で新郎新婦が離席し、衣装を変えること", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12373, "", "剪定ばさみ", "剪定ばさみ", "これ、何と言う？", 12415, 0, 6, 12400, 6, 21098, 23450, 5, 5179, 4, "庭木を切って整えるためのはさみで、普通のはさみなら切れない太い枝なども切ることができる", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "クーリング", "クーリング・オフ", "これ、何と言う？", 12464, 0, 5, 12522, 5, 12463, 12540, 5, 5180, 5, "訪問販売などで考える暇なく購入してしまったものを、一定期間中ならキャンセルできる制度", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12435, "", "ねこまんま", "ねこまんま", "これ、何と言う？", 12414, 0, 4, 12414, 4, 12397, 12371, 5, 5181, 4, "「猫にあげるような飯」のこと。削り節をかけるか、味噌汁をかけるか、定義は地域で分かれる", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12519, "", "おくション", "おくション", "これ、何と言う？", 12531, 0, 4, 12471, 4, 12362, 12367, 5, 5182, 5, "分譲マンションで、価格が1億円を超えるような特に高級なものを指す俗語", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "パーキング", "パーキングエリア", "これ、何と言う？", 12464, 0, 5, 12461, 5, 12497, 12540, 5, 5183, 5, "高速道路で、「SA」は「サービスエリア」。では「PA」は？", 5, 1, 5);
