INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12470, "", "テナガザル", "テナガザル", "これ、何と言う？", 12523, 0, 5, 12460, 5, 12486, 12490, 6, 6012, 5, "猿の種類の一つ。その名の通り長い手を使って、樹上を軽やかに動く", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12464, "", "アライグマ", "アライグマ", "これ、何と言う？", 12510, 0, 5, 12452, 5, 12450, 12521, 6, 6013, 5, "哺乳類の一種。水に直接手を突っ込んで獲物を取る様子が「洗っている」ように見える", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12460, "", "ミドリガメ", "ミドリガメ", "これ、何と言う？", 12513, 0, 5, 12522, 5, 12511, 12489, 6, 6014, 5, "お祭りや縁日でも親しまれてきたカメの通称。幼い頃の甲羅の色に由来している", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "カンガルー", "カンガルー", "これ、何と言う？", 12540, 0, 5, 12460, 5, 12459, 12531, 6, 6015, 5, "ジャンプが得意な、哺乳類の一種。お腹の袋で子育てをすることでよく知られる", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12471, "", "キャッシュ", "クレジッタ・キャッシュ", "これ、何と言う？", 12517, 6020, 5, 12483, 5, 12461, 12515, 6, 6020, 5, "【リッチモンド商工会】のギルドマスターの『クレジッタ』。フルネームはなんと言う？", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 35242, "", "ドクロ親父", "ドクロ親父", "この人は誰？", 29238, 6021, 5, 12525, 5, 12489, 12463, 6, 6021, 6, "『シノブ』の父親の通称。過去の霊媒の仕事で、ドクロ姿になってしまったことから", 5, 2, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12419, "", "ドラちゃん", "ドラちゃん", "これ、何と言う？", 12435, 6022, 5, 12385, 5, 12489, 12521, 6, 6022, 4, "エルピス島で発見された生物。正しくは『ドラゴンもどき』だが、『イノリ』からの呼ばれ方は？", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12371, "", "すこすこ侍", "カルミナすこすこ侍", "この人は誰？", 20365, 6024, 4, 12377, 4, 12377, 12371, 6, 6024, 6, "チエルの、「カルミナファン」としての二つ名", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 20901, "", "疾風の冥姫", "疾風の冥姫", "これ、何と言う？", 23019, 3002, 6, 12398, 6, 30142, 39080, 6, 6025, 6, "『アンナ』の二つ名。後ろの2文字はそのままでは読まず、「ヘカーテ」と読むらしい", 4, 2, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12377, "", "ママですよ", "ほぉら、ママですよ～", "これ、何と書いてある？", 12424, 6028, 5, 12391, 5, 12510, 12510, 6, 6028, 4, "『ミサト』を★5にしたときに獲得できる称号", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12497, "", "テンタパス", "テンタパス", "これ、何と言う？", 12473, 6041, 5, 12479, 5, 12486, 12531, 6, 6041, 5, "イベント「デンジャラスバカンス！　渚のグルメプリンセス」のボス", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 22899, "", "嘆きの女神", "嘆きの女神", "これ、何と言う？", 31070, 6042, 4, 12398, 6, 22022, 12365, 6, 6042, 6, "イベント「王都の名探偵 嘆きの追跡者」のボス", 4, 1, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12488, "", "ファントム", "ファントムバロン", "これ、何と言う？", 12512, 6043, 5, 12531, 5, 12501, 12449, 6, 6043, 5, "イベント「リトル・ブレイブ・ハロウィンナイト！」のボス", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12412, "", "うわきぼし", "覚醒・うわきぼし", "これ、何と言う？", 12375, 6044, 4, 12365, 4, 12358, 12431, 6, 6044, 4, "イベント「七夕剣客旅情譚　天に流れる夏の恋」のボス", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12492, "", "トゥンヌス", "グレートトゥンヌス", "これ、何と言う？", 12473, 6045, 5, 12531, 5, 12488, 12453, 6, 6045, 5, "イベント「新春グルメプリンセス！　一投にかけた乙女たち」のボス", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12521, "", "ウタクラゲ", "クイーンウタクラゲ", "これ、何と言う？", 12466, 6046, 5, 12463, 5, 12454, 12479, 6, 6046, 5, "イベント「カルミナサマーライブ　ジャスト　ア　モーメント！」のボス", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12486, "", "トリニティ", "アンチビースト・トリニティ", "これ、何と言う？", 12451, 6047, 5, 12491, 5, 12488, 12522, 6, 6047, 5, "イベント「魔法少女外伝　マジカル★ノワールズ！」のボス", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12487, "大人のレディ", "大人レディ", "大人のレディ", "これ、何と言う？", 12451, 6048, 6, 12524, 6, 22823, 20154, 6, 6048, 5, "『ミサキ』がどのような女性であるかを表した通称。背伸び感が可愛らしい", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12463, "", "Tレックス", "Tレックス", "これ、何と言う？", 12473, 0, 5, 12483, 2, 84, 12524, 6, 6049, 5, "『ティラノサウルス』の別の言い方", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12503, "", "ケラトプス", "トリケラトプス", "これ、何と言う？", 12473, 0, 5, 12488, 5, 12465, 12521, 6, 6050, 5, "3本の立派なツノが特徴の、有名な恐竜。名前の読み方は、「トリ」で区切るのが実は正しい", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12519, "エコーロケーション", "ロケション", "エコーロケーション", "これ、何と言う？", 12531, 0, 5, 12471, 5, 12525, 12465, 6, 6051, 5, "イルカやコウモリなどが持つ能力。超音波を発し、その反響によって周囲の様子を探る", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12525, "マングローブ", "マングロブ", "マングローブ", "これ、何と言う？", 12502, 0, 5, 12464, 5, 12510, 12531, 6, 6052, 5, "熱帯や亜熱帯の一部の水辺に生育する植物の総称で、非常にユニークな形の根っこを形成する", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 24565, "", "天然記念物", "天然記念物", "これ、何と言う？", 29289, 0, 6, 35352, 6, 22825, 28982, 6, 6053, 6, "学術的に貴重なものとして保護される、天然の動物・植物・地質・鉱物。アホウドリやトキなど", 6, 1, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12414, "", "つちふまず", "つちふまず", "これ、何と言う？", 12378, 0, 4, 12405, 4, 12388, 12385, 6, 6054, 4, "人間の足の裏の、地面と接しないくぼんだ部分。人によっては無い場合もある", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12488, "", "ファントム", "ファントムキャッツ", "これは何？", 12512, 5036, 5, 12531, 5, 12501, 12449, 6, 6055, 5, "『タマキ』の裏の名前にして、恵まれない人たちを助ける、謎に包まれた義賊", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12452, "", "ワンアイド", "隻眼の赤い悪魔\n＝ワンアイドデビル", "これ、何と読む？", 12489, 6056, 5, 12450, 5, 12527, 12531, 6, 6056, 5, "裏社会で恐れられる『ミツキ』の二つ名。彼女の隻眼からは逃れられない", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12479, "", "コレクター", "蒐集家（＝コレクター）", "これ、何と読む？", 12540, 6057, 5, 12463, 5, 12467, 12524, 6, 6057, 5, "『ナナカ』の二つ名。さまざまな魔法を探し集めていることに由来する", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12531, "", "レグナント", "変貌大妃（＝メタモルレグナント）", "これ、何と読む？", 12488, 6058, 5, 12490, 5, 12524, 12464, 6, 6058, 5, "『ネネカ』の、『七冠』としての二つ名。あらゆるものに姿を変化させられることから", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12496, "", "ケンカバカ", "脳筋ケンカバカ", "これ、何と言う？", 12459, 6059, 5, 12459, 5, 12465, 12531, 6, 6059, 5, "『イノリ』が『カヤ』に対して、ムキになったり、おちょくったりするときの呼び方", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12524, "サラブレッド", "サラブレド", "サラブレッド", "これ、何と言う？", 12489, 0, 5, 12502, 5, 12469, 12521, 6, 6060, 5, "レースのために、長い年月をかけて改良された類の馬。家柄の良い人のたとえ表現でもおなじみ", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12484, "", "スナギツネ", "チベットスナギツネ", "これ、何と言う？", 12493, 0, 5, 12462, 5, 12473, 12490, 6, 6061, 5, "キツネの一種。冷ややかで悟りを開いたような表情に見えることで近年人気を博している", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "シーラカンス", "シラカンス", "シーラカンス", "これ、何と言う？", 12473, 0, 5, 12459, 5, 12471, 12521, 6, 6062, 5, "数億年も前から変わらない姿のまま現存しているため、「生きた化石」と呼ばれる深海魚", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12524, "", "ヘラクレス", "ヘラクレスオオカブト", "これ、何と言う？", 12473, 0, 5, 12463, 5, 12504, 12521, 6, 6063, 5, "カブトムシの中で最大のサイズを誇る種として、子どもたちからも絶大な人気を誇る昆虫", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12512, "", "アブラムシ", "アブラムシ", "これ、何と言う？", 12471, 0, 5, 12521, 5, 12450, 12502, 6, 6064, 5, "昔の子どもたちがこの虫の体液を髪に塗り、テカテカさせて遊んでいたことに由来する虫の名前", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12367, "", "泣きぼくろ", "泣きぼくろ", "これ、何と言う？", 12429, 0, 4, 12412, 6, 27875, 12365, 6, 6065, 4, "目のすぐ下のあたりにあるほくろの通称", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12496, "シルバーバック", "シルババク", "シルバーバック", "これ、何と言う？", 12463, 0, 5, 12496, 5, 12471, 12523, 6, 6066, 5, "年を重ねたオスのゴリラの背中の毛が白くなる現象。群れの中のボス個体によく見られる", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12458, "", "カメレオン", "カメレオン", "これ、何と言う？", 12531, 0, 5, 12524, 5, 12459, 12513, 6, 6067, 5, "身体の色を自在に変化させることでおなじみの爬虫類。演技の幅が広い俳優や声優のたとえにも", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "オニヤンマ", "オニヤンマ", "これ、何と言う？", 12510, 0, 5, 12516, 5, 12458, 12491, 6, 6068, 5, "非常に大きなトンボの一種。「鬼」の服装とよく似た、黒と黄色のシマシマ模様が特徴的", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "グルーミング", "グルミング", "グルーミング", "これ、何と言う？", 12464, 0, 5, 12511, 5, 12464, 12523, 6, 6069, 5, "猫などの動物の習性として知られる「毛づくろい」。英語でなんと言う？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "マングース", "マングース", "これ、何と言う？", 12473, 0, 5, 12464, 5, 12510, 12531, 6, 6070, 5, "一般に「ハブ」の天敵というイメージを持たれている、体長数十センチほどの哺乳類", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "ギンヤンマ", "ギンヤンマ", "これ、何と言う？", 12510, 0, 5, 12516, 5, 12462, 12531, 6, 6071, 5, "トンボの一種。身体は黄緑色だが、よく見るとキレイな銀色の模様があることからこう呼ばれる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "カモミール", "カモミール", "これ、何と言う？", 12523, 0, 5, 12511, 5, 12459, 12514, 6, 6072, 5, "「カミツレ」とも呼ばれるハーブの一種。りんごのような香りがあり、お茶としてよく好まれる", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12452, "", "ホタルイカ", "ホタルイカ", "これ、何と言う？", 12459, 0, 5, 12523, 5, 12507, 12479, 6, 6073, 5, "指先ほどのサイズの小さなイカ。青白く発光する性質があり、また食用としても好まれる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12480, "", "キンメダイ", "キンメダイ", "これ、何と言う？", 12452, 0, 5, 12513, 5, 12461, 12531, 6, 6074, 5, "高級魚や縁起物としておなじみのタイの一種。名前は、目が金色に輝くことから", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12512, "", "グソクムシ", "ダイオウグソクムシ", "これ、何と言う？", 12471, 0, 5, 12463, 5, 12464, 12477, 6, 6075, 5, "深海に生息する甲殻類。姿はダンゴムシに似ていて、大きなものは体長50cmを超える", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12490, "", "チンアナゴ", "チンアナゴ", "これ、何と言う？", 12468, 0, 5, 12450, 5, 12481, 12531, 6, 6076, 5, "水族館でも人気なアナゴの一種。砂底から身体を出し、群れで同じ方向をじっと向く姿が特徴的", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12471, "", "ラフレシア", "ラフレシア", "これ、何と言う？", 12450, 0, 5, 12524, 5, 12521, 12501, 6, 6077, 5, "「世界最大の花」としておなじみの、熱帯地域に生息する花。強い悪臭を放つことでも有名", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "ベニテング", "ベニテングタケ", "これ、何と言う？", 12464, 0, 5, 12486, 5, 12505, 12491, 6, 6078, 5, "真っ赤なカサに白い点のある、キノコの一種。毒があり、食べると幻覚・腹痛などの症状が出る", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12474, "", "コノハズク", "コノハズク", "これ、何と言う？", 12463, 0, 5, 12495, 5, 12467, 12494, 6, 6079, 5, "「フクロウ」の一種。身の危険を感じたときに身体を細く縮めて、木の枝に擬態する習性がある", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12519, "", "サンショウ", "サンショウウオ", "これ、何と言う？", 12454, 0, 5, 12471, 5, 12469, 12531, 6, 6080, 5, "両生類の一種。防衛のために出す粘液の匂いが、和食に使うとあるスパイスに似ているのが由来", 5, 3, 5);
