INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12513, "", "フレーメン", "フレーメン反応", "これ、何と言う？", 12531, 0, 5, 12540, 5, 12501, 12524, 6, 6167, 5, "猫などの動物が強い匂いを嗅いだときに、目や口を開いて独特な表情をする現象", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12489, "", "メガロドン", "メガロドン", "これ、何と言う？", 12531, 0, 5, 12525, 5, 12513, 12460, 6, 6168, 5, "太古の海に存在していた、史上最大のサメ", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12426, "", "いときりば", "いときりば", "これ、何と言う？", 12400, 0, 4, 12365, 4, 12356, 12392, 6, 6169, 4, "「犬歯」の通称として、「八重歯」以外になじみ深いもの。糸が切れるほど尖っているのが由来", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12519, "カーネーション", "カネション", "カーネーション", "これ、何と言う？", 12531, 0, 5, 12471, 5, 12459, 12493, 6, 6170, 5, "「母の日」の贈り物としても親しまれる花。赤やピンクの鮮やかな花びらを持つ", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12471, "", "メイヨシノ", "ソメイヨシノ", "これ、何と言う？", 12494, 0, 5, 12520, 5, 12513, 12452, 6, 6171, 5, "桜の木の種類として非常にポピュラーな一つ。一般に桜のイメージとして最も浸透している", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12458, "", "デライオン", "ダンデライオン", "これ、何と言う？", 12531, 0, 5, 12452, 5, 12487, 12521, 6, 6172, 5, "「タンポポ」の英名。とある哺乳類の動物に由来している", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12421, "", "せいりゅう", "せいりゅう", "これ、何と言う？", 12358, 0, 4, 12426, 4, 12379, 12356, 6, 6173, 4, "「四聖獣」と呼ばれる生き物、朱雀・白虎・玄武、あとは何？（ひらがなで）", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12425, "", "座敷わらし", "座敷わらし", "これ、何と言う？", 12375, 0, 6, 12431, 6, 24231, 25975, 6, 6174, 4, "小さな子どもの姿で、座敷や土間に現れる妖怪。遭遇した者には幸運が舞い込むとされる", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12459, "", "マンチカン", "マンチカン", "これ、何と言う？", 12531, 0, 5, 12481, 5, 12510, 12531, 6, 6175, 5, "猫の種類の一つ。「小さい」「子ども」という意味を持ち、小柄で脚が短いのが特徴", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12495, "", "デュラハン", "デュラハン", "これ、何と言う？", 12531, 0, 5, 12521, 5, 12487, 12517, 6, 6176, 5, "伝承に登場する、首の無い騎士", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12450, "", "オッドアイ", "オッドアイ", "これ、何と言う？", 12452, 0, 5, 12489, 5, 12458, 12483, 6, 6177, 5, "両目でそれぞれ瞳の色が異なること。いろいろな動物に見られるが、特に猫に多い", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12425, "", "猫じゃらし", "猫じゃらし", "これ、何と言う？", 12375, 0, 4, 12419, 6, 29483, 12376, 6, 6178, 4, "「エノコログサ」という草の通称。猫の目の前で振ると興味を示し、よくじゃれつくことから", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12517, "", "ドラキュラ", "ドラキュラ", "これ、何と言う？", 12521, 0, 5, 12461, 5, 12489, 12521, 6, 6179, 5, "非常によく知られた怪物。獲物の血を吸うのが特徴で、十字架やニンニクが弱点とされる", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12460, "", "ンコロガシ", "フンコロガシ", "これ、何と言う？", 12471, 0, 5, 12525, 5, 12531, 12467, 6, 6180, 5, "動物のフンをボール状にして、コロコロ転がすという変わった習性を持つ昆虫", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12540, "", "ラグドール", "ラグドール", "これ、何と言う？", 12523, 0, 5, 12489, 5, 12521, 12464, 6, 6181, 5, "「ぬいぐるみ」という意味を持つ、猫の種類。抱っこが好きで、毛並みがフサフサなことから", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12481, "エキゾチック・ショートヘア", "エキゾチク", "エキゾチック・ショートヘア", "これ、何と言う？", 12463, 0, 5, 12478, 5, 12456, 12461, 6, 6182, 5, "猫の種類の一つ。鼻が低く、ムスッとしたような愛嬌のある顔立ちが特徴的", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12400, "", "んしょばと", "でんしょばと", "これ、何と言う？", 12392, 0, 4, 12423, 4, 12435, 12375, 6, 6183, 4, "ハトの帰巣本能を使って遠くに手紙を届けること。通信技術が発達する前は、広く活用された", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12461, "ホーホケキョ", "ホホケキョ", "ホーホケキョ", "これ、何と言う？", 12519, 0, 5, 12465, 5, 12507, 12507, 6, 6184, 5, "「ウグイス」の鳴き声を文字に起こした表現。春の訪れの象徴としてもなじみ深い", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12462, "", "ホトトギス", "ホトトギス", "これ、何と言う？", 12473, 0, 5, 12488, 5, 12507, 12488, 6, 6185, 5, "武将の性格を比喩した「鳴かぬなら～～」の俳句に使われている鳥の名前は？", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12481, "", "ヘビイチゴ", "ヘビイチゴ", "これ、何と言う？", 12468, 0, 5, 12452, 5, 12504, 12499, 6, 6186, 5, "「ヘビがいそうな場所に生える」ことに由来する、イチゴに似た植物。食用には向かない", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12367, "", "松ぼっくり", "松ぼっくり", "これ、何と言う？", 12426, 0, 4, 12387, 6, 26494, 12412, 6, 6187, 4, "松かさとも呼ばれる、概略すると松の木の果実の部分。クリスマスツリーの飾りにもよく使われる", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12523, "", "アゾールド", "アゾールド", "このキャラの名前は？", 12489, 6188, 5, 12540, 5, 12450, 12478, 6, 6188, 5, "", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12423, "", "ズナちょむ", "ズナちょむ", "左のキャラは右のキャラを何と呼ぶ？", 12416, 6189, 5, 12385, 5, 12474, 12490, 6, 6189, 4, "", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "フォールド", "スコティッシュフォールド", "これ、何と言う？", 12489, 0, 5, 12540, 5, 12501, 12457, 6, 6190, 5, "猫の種類の一つ。垂れた耳が特徴的", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12487, "", "ゴールデン", "ゴールデンレトリーバー", "これ、何と言う？", 12531, 0, 5, 12523, 5, 12468, 12540, 6, 6191, 5, "非常にポピュラーな種の犬で、フサフサとした黄色系の体毛と、大きな体が特徴", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12521, "", "ドッグラン", "ドッグラン", "これ、何と言う？", 12531, 0, 5, 12464, 5, 12489, 12483, 6, 6192, 5, "家庭犬がリードをつけずに自由に走り回れるように設けられた、フェンスで囲われた広いスペース", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12489, "ラブラドールレトリーバー", "ラブラドル", "ラブラドールレトリーバー", "これ、何と言う？", 12523, 0, 5, 12521, 5, 12521, 12502, 6, 6193, 5, "レトリーバー種としてゴールデンレトリーバーに並び有名な犬。盲導犬・警察犬としても活躍する", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "マーキング", "マーキング", "これ、何と言う？", 12464, 0, 5, 12461, 5, 12510, 12540, 6, 6194, 5, "動物が自分の体臭や尿などを残すことで、そこが自分のなわばりであるとアピールする習性", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 25345, "", "たいこ持ち", "たいこ持ち", "これ、何と言う？", 12385, 0, 4, 12371, 4, 12383, 12356, 6, 6195, 4, "しきりに偉い人のご機嫌をとるような人間を、ある楽器の名前を使ってなんと呼ぶ？", 4, 2, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12522, "", "キリギリス", "キリギリス", "これ、何と言う？", 12473, 0, 5, 12462, 5, 12461, 12522, 6, 6196, 5, "有名な童話の中で、「アリ」と対比して、なまけ者として描かれている昆虫", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12456, "", "クルマエビ", "クルマエビ", "これ、何と言う？", 12499, 0, 5, 12510, 5, 12463, 12523, 6, 6197, 5, "エビの一種。身体にクッキリとシマシマがあり、丸まった姿が車のホイールに見える", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12519, "", "ダイショウ", "アオダイショウ", "これ、何と言う？", 12454, 0, 5, 12471, 5, 12480, 12452, 6, 6198, 5, "ヘビの一種で、身体は青緑色。由来は諸説あるが「ヘビの中でも特に大物の風格がある」ことなど", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12423, "", "しそちょう", "しそちょう", "これ、何と言う？", 12358, 0, 4, 12385, 4, 12375, 12381, 6, 6199, 4, "太古の時代に存在した鳥で、その名の通り、鳥類の始まりとなった鳥とされている", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12488, "", "アウストラ", "アウストラロピテクス", "これ、何と言う？", 12521, 0, 5, 12473, 5, 12450, 12454, 6, 6200, 5, "「最古の人類」とされている種類の猿人", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12459, "", "キュウカン", "キュウカンチョウ", "これ、何と言う？", 12531, 0, 5, 12454, 5, 12461, 12517, 6, 6201, 5, "ペットとしても親しまれる手乗りサイズの鳥で、人の声をマネして鳴くのが上手い", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12475, "", "オットセイ", "オットセイ", "これ、何と言う？", 12452, 0, 5, 12488, 5, 12458, 12483, 6, 6202, 5, "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12478, "", "アジアゾウ", "アジアゾウ", "これ、何と言う？", 12454, 0, 5, 12450, 5, 12450, 12472, 6, 6203, 5, "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12472, "", "ベニシジミ", "ベニシジミ", "これ、何と言う？", 12511, 0, 5, 12471, 5, 12505, 12491, 6, 6204, 5, "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12469, "", "ジュウサン", "ジュウサンホシテントウ", "これ、何と言う？", 12531, 0, 5, 12454, 5, 12472, 12517, 6, 6205, 5, "テントウムシの一種。身体に13個の点々模様がある", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12454, "", "ニジュウヤ", "ニジュウヤホシテントウ", "これ、何と言う？", 12516, 0, 5, 12517, 5, 12491, 12472, 6, 6206, 5, "テントウムシの一種。身体に28個の点々模様がある", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12461, "", "アニサキス", "アニサキス", "これ、何と言う？", 12473, 0, 5, 12469, 5, 12450, 12491, 6, 6207, 5, "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12393, "", "わたりどり", "わたりどり", "これ、何と言う？", 12426, 0, 4, 12426, 4, 12431, 12383, 6, 6208, 4, "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "フンボルト", "フンボルトペンギン", "これ、何と言う？", 12488, 0, 5, 12508, 5, 12501, 12531, 6, 6209, 5, "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12489, "", "エボシドリ", "エボシドリ", "これ、何と言う？", 12522, 0, 5, 12471, 5, 12456, 12508, 6, 6210, 5, "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "ネザーランドドワーフ", "ネザランド", "ネザーランドドワーフ", "これ、何と言う？", 12489, 0, 5, 12521, 5, 12493, 12470, 6, 6211, 5, "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "ホーランド", "ホーランドロップ", "これ、何と言う？", 12489, 0, 5, 12521, 5, 12507, 12540, 6, 6212, 5, "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12483, "", "フェレット", "フェレット", "これ、何と言う？", 12488, 0, 5, 12524, 5, 12501, 12455, 6, 6213, 5, "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12498, "", "マントヒヒ", "マントヒヒ", "これ、何と言う？", 12498, 0, 5, 12488, 5, 12510, 12531, 6, 6214, 5, "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12460, "", "シロナガス", "シロナガスクジラ", "これ、何と言う？", 12473, 0, 5, 12490, 5, 12471, 12525, 6, 6215, 5, "最も大きい動物としてよく知られる種類のクジラ", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "シロイルカ", "シロイルカ", "これ、何と言う？", 12459, 0, 5, 12452, 5, 12471, 12525, 6, 6216, 5, "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12373, "", "しろうさぎ", "いなばのしろうさぎ", "これ、何と言う？", 12366, 0, 4, 12358, 4, 12375, 12429, 6, 6217, 4, "神話に登場する、有名な動物", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12458, "さそり＝スコーピオン", "スコピオン", "さそり＝スコーピオン", "これ、何と言う？", 12531, 0, 5, 12500, 5, 12473, 12467, 6, 6218, 5, "カタカナでの呼び方は？", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12371, "", "ちからこぶ", "ちからこぶ", "これ、何と言う？", 12406, 0, 4, 12425, 4, 12385, 12363, 6, 6219, 4, "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12395, "", "さるかに合", "さるかに合戦", "これ、何と言う？", 21512, 0, 4, 12363, 4, 12373, 12427, 6, 6220, 6, "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "チンパンジ", "♪サル、ゴリラ、チンパンジー", "これ、何と言う？", 12472, 0, 5, 12497, 5, 12481, 12531, 6, 6221, 5, "よく親しまれた替え歌の歌詞。ある動物の名前が入る", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12477, "", "アオイソメ", "アオイソメ", "これ、何と言う？", 12513, 0, 5, 12452, 5, 12450, 12458, 6, 6222, 5, "釣り人には釣り餌として非常におなじみの、ミミズのような虫", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12479, "", "鋼メンタル", "鋼メンタル", "これ、何と言う？", 12523, 0, 5, 12531, 6, 37628, 12513, 6, 6223, 5, "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12473, "", "マゾヒスト", "マゾヒスト", "これ、何と言う？", 12488, 6224, 5, 12498, 5, 12510, 12478, 6, 6224, 5, "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12523, "", "コアトルス", "ケツァルコアトルス", "これ、何と言う？", 12473, 0, 5, 12488, 5, 12467, 12450, 6, 6225, 5, "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12496, "", "アナツバメ", "アナツバメ", "これ、何と言う？", 12513, 0, 5, 12484, 5, 12450, 12490, 6, 6226, 5, "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12519, "", "タンチョウ", "タンチョウヅル", "これ、何と言う？", 12454, 0, 5, 12481, 5, 12479, 12531, 6, 6227, 5, "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12486, "", "ルリタテハ", "ルリタテハ", "これ、何と言う？", 12495, 0, 5, 12479, 5, 12523, 12522, 6, 6228, 5, "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12475, "", "プリンセス", "プリンセスナイト", "これ、何と言う？", 12473, 6229, 5, 12531, 5, 12503, 12522, 6, 6229, 5, "特定の相手の能力\nを高める権能を持\nった存在", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12377, "", "ネコむすめ", "ネコむすめ", "このパネルの「プリコネ読み」は？", 12417, 6230, 5, 12416, 5, 12493, 12467, 6, 6230, 4, "", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 24807, "", "絶滅危惧種", "絶滅危惧種", "これ、何と言う？", 31278, 0, 6, 21361, 6, 32118, 28357, 6, 6231, 6, "数が減少し、絶滅の危機に晒されている生物", 6, 2, 6);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12476, "", "アブラゼミ", "アブラゼミ", "これ、何と言う？", 12511, 0, 5, 12521, 5, 12450, 12502, 6, 6232, 5, "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12473, "", "アキレス腱", "アキレス腱", "これ、何と言う？", 33137, 0, 5, 12524, 5, 12450, 12461, 6, 6233, 6, "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12473, "", "ナルシスト", "ナルシスト", "これ、何と言う？", 12488, 6234, 5, 12471, 5, 12490, 12523, 6, 6234, 5, "自己愛が非常に強く、自分に陶酔した人、英語にすると？", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12423, "", "だんちょう", "だんちょう", "左のキャラは右のキャラを何と呼ぶ？", 12358, 6235, 4, 12385, 4, 12384, 12435, 6, 6235, 4, "", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12378, "", "いいなずけ", "許嫁＝いいなずけ", "これ、何と読む？", 12369, 0, 4, 12394, 4, 12356, 12356, 6, 6236, 4, "若いうちから結婚が約束された相手のこと", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12425, "", "やたがらす", "やたがらす", "これ、何と言う？", 12377, 0, 4, 12364, 4, 12420, 12383, 6, 6237, 4, "「古事記」などに登場する、三本足の神聖なカラス", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12423, "", "さごじょう", "さごじょう", "これ、何と言う？", 12358, 0, 4, 12376, 4, 12373, 12372, 6, 6238, 4, "「西遊記」で孫悟空と行動を共にする、河童の姿のキャラクター", 4, 2, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12521, "", "ディーラー", "ディーラー", "これ、何と言う？", 12540, 0, 5, 12540, 5, 12487, 12451, 6, 6239, 5, "カジノで、ルーレットを回したりカードを配ったりとゲームの進行を行う役目", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12524, "", "エンプレス", "エンプレス", "これ、何と言う？", 12473, 0, 5, 12503, 5, 12456, 12531, 6, 6240, 5, "「皇帝」は「エンペラー」。では「女帝」は？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12511, "", "ガンスミス", "ガンスミス", "これ、何と言う？", 12473, 0, 5, 12473, 5, 12460, 12531, 6, 6241, 5, "銃のメンテナンスを職業として行う人のこと", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12473, "", "ハイビスカ", "ハイビスカス", "これ、何と言う？", 12459, 0, 5, 12499, 5, 12495, 12452, 6, 6242, 5, "熱帯の地域を象徴する花で、カオリの故郷でも親しまれる。赤や黄色の鮮やかな花びらをつける", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12473, "ウィル・オ・ウィスプ", "オウィスプ", "ウィル・オ・ウィスプ", "これ、何と言う？", 12503, 0, 5, 12451, 5, 12458, 12454, 6, 6243, 5, "「ひとだま」と呼ばれる超常現象、英語で言うと？", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12468, "", "ニシキゴイ", "ニシキゴイ", "これ、何と言う？", 12452, 0, 5, 12461, 5, 12491, 12471, 6, 6244, 5, "魚の「鯉」の中でも観賞用として好まれる種で、高級な織物の柄によく似ていることが由来", 5, 2, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "レッサーパンダ", "レサパンダ", "レッサーパンダ", "これ、何と言う？", 12480, 0, 5, 12497, 5, 12524, 12469, 6, 6245, 5, "「小さなパンダ」が由来の、動物園の人気者", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12531, "", "アナコンダ", "アナコンダ", "これ、何と言う？", 12480, 0, 5, 12467, 5, 12450, 12490, 6, 6246, 5, "ヘビの一種で、大きなものでは数メートルもある。映画で恐ろしいヘビとして描かれることが多い", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12481, "", "タランチュ", "タランチュラ", "これ、何と言う？", 12517, 0, 5, 12531, 5, 12479, 12521, 6, 6247, 5, "「毒グモの代名詞」としてよく知られる大型のクモ。しかし、ペットとして好む人も多い", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12517, "", "スイギュウ", "スイギュウ", "これ、何と言う？", 12454, 0, 5, 12462, 5, 12473, 12452, 6, 6248, 5, "「牛」の一種で、地域によって家畜としてもなじみ深い動物。その名の通り、水辺を好む", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12479, "", "ハムスター", "ハムスター", "これ、何と言う？", 12540, 0, 5, 12473, 5, 12495, 12512, 6, 6249, 5, "ネズミの仲間にあたる動物で、ヒマワリの種などを口いっぱいに頬張る姿が象徴的", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12474, "", "ハリネズミ", "ハリネズミ", "これ、何と言う？", 12511, 0, 5, 12493, 5, 12495, 12522, 6, 6250, 5, "身を守るためのトゲトゲの体毛が非常に特徴的な動物。愛好家も多く、英名は「ヘッジホッグ」", 5, 1, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12514, "", "ヒョウモン", "ヒョウモンダコ", "これ、何と言う？", 12531, 0, 5, 12454, 5, 12498, 12519, 6, 6251, 5, "猛毒を持つタコ。蛍光色のヒョウ柄の模様が、恐ろしさを助長している", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12458, "", "カメレオン", "カメレオン", "これ、何と言う？", 12531, 6252, 5, 12524, 5, 12459, 12513, 6, 6252, 5, "ネネカといつも一緒にいる動物", 5, 3, 5);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 12419, "", "アイちゃん", "アイちゃん", "これ、何と言う？", 12435, 6048, 5, 12385, 5, 12450, 12452, 6, 6253, 4, "『ミサキ』がいつも一緒にいる、生きた杖の名前", 4, 3, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (2, 12421, "", "にくきゅう", "にくきゅう", "これ、何と言う？", 12358, 0, 4, 12365, 4, 12395, 12367, 6, 6254, 4, "ネコ目の動物の足にある、ぷにぷにとやわらかい部分の通称", 4, 1, 4);
INSERT INTO `v1_8f4efbf7774d42dc4a6b0d1dc36da7c6d954f95a803e96c044adca210afe9c4f` VALUES (1, 26681, "", "諸悪の根源", "諸悪の根源", "初対面のとき何と呼んだ？", 28304, 6255, 6, 12398, 6, 35576, 24746, 6, 6255, 6, "", 4, 3, 6);
