INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 1, 3, "やばいですね☆なの！", "ペコリーヌのプリン", 100, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 2, 3, "主さま、なの！", "コッコロのプリン", 101, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 3, 3, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "キャルのプリン", 102, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 4, 3, "おにーたん♪なの！", "シェフィのプリン", 103, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 5, 3, "ハーハッハッハ！\n世界征服なのじゃなのー！", "イリヤのプリン", 104, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 6, 3, "お兄ちゃんのエッチ！　なの！", "アカリのプリン", 105, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 7, 3, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "ヨリのプリン", 106, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 8, 3, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "シノブのプリン", 107, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 9, 3, "インフェルノシールド！なの！", "ジュンのプリン", 108, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 10, 3, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "トモのプリン", 109, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 11, 3, "タイガーヒーローボンバー！\nなの！", "マツリのプリン", 110, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 13, 3, "ま、いいんじゃん？\nしらんけどなの", "クロエのプリン", 111, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 14, 3, "ぷりーん★なの！", "チエルのプリン", 112, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 15, 3, "端的に換言すれば\n青春の味なの？", "ユニのプリン", 113, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 16, 3, "幼なじみはあんた…なの？", "サレンのプリン", 114, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 17, 3, "ひゃああんなの～！", "スズメのプリン", 115, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 18, 3, "サルキデバレヤ！なの！", "クルミのプリン", 116, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 19, 3, "プリンフルスイングなの～！", "アヤネのプリン", 117, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 20, 3, "みらくるみやりん\nくるりんぱ～☆なの！", "マホのプリン", 118, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 21, 3, "ウルフェンバイトなの！", "マコトのプリン", 119, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 22, 3, "なんくるないさ～！なの！", "カオリのプリン", 120, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 23, 3, "ふふん。簡単な推理なの", "カスミのプリン", 121, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 24, 3, "エリザベス～！なの～！", "マヒルのプリン", 122, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 25, 3, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "シオリのプリン", 123, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 26, 3, "もふもふにしてあげるなの！", "リマのプリン", 124, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 27, 3, "めんどくさいな～なの…", "リンのプリン", 125, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 28, 3, "プリンの権能を解放するの～！", "ラビリスタのプリン", 126, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 29, 3, "お姉ちゃんパワー全開！なの！", "シズルのプリン", 127, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 30, 3, "お兄ちゃん、プリンをよこすの！", "リノのプリン", 128, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 31, 3, "困った時はお互いさまさま！\nなの！", "ヒヨリのプリン", 129, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 32, 3, "フラワリングヒールなの～♪", "ユイのプリン", 130, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 33, 3, "スラッシュテンペスト！なの。", "レイのプリン", 131, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 34, 3, "おーほっほほほ！なの！", "アキノのプリン", 132, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 35, 3, "プリしゅわ最高～！なの！", "ユカリのプリン", 133, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 36, 3, "にゃにゃにゃ！なの！", "タマキのプリン", 134, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 37, 3, "効率全開！なの！", "ミフユのプリン", 135, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 38, 3, "あらあら～なの～♪", "ミサトのプリン", 136, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 39, 3, "スヤスヤ～…なの…", "ハツネのプリン", 137, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 40, 3, "ぼっちは卒業なの～！", "アオイのプリン", 138, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 41, 3, "リズムあげるのー！", "ノゾミのプリン", 139, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 42, 3, "風の加護を！なの！", "チカのプリン", 140, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 43, 3, "騎士さんのために\nしたんじゃありません！なの！", "ツムギのプリン", 141, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 12, 3, "宴の始まりなの！", "クリスティーナのプリン", 142, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 44, 3, "一刀両断、覚悟しな！なの！", "ルカのプリン", 143, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 45, 3, "きゅぴーん！キタコレ！なの！", "ナナカのプリン", 144, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 46, 3, "人呼んで、疾風のミヤコなの！", "アンナのプリン", 145, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 47, 3, "クスクス…クスクスクス…なの", "エリコのプリン", 146, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 48, 3, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "ミツキのプリン", 147, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 49, 3, "私は子どもではない！なの！", "モニカのプリン", 148, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 50, 3, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "ニノンのプリン", 149, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 51, 3, "…ぐふふ、じゅるり～なの", "クウカのプリン", 150, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 52, 3, "あぁ、ミヤコはなんて美しいの～", "ユキのプリン", 151, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 53, 3, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "アユミのプリン", 152, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 54, 3, "にひひ…イタズラの時間なの！", "ミソギのプリン", 153, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 55, 3, "ぴょんぴょこぴょーん、なの♪", "ミミのプリン", 154, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 56, 3, "へんたいふしんしゃさんなの！", "キョウカのプリン", 155, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 57, 3, "ハート狙い撃ち！なの！", "スズナのプリン", 156, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 58, 3, "授業を始めます♪なの！", "イオのプリン", 157, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 59, 3, "立派なレディ、なの！", "ミサキのプリン", 158, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 60, 3, "プリンを持ってこないと\nお仕置きだよ～☆なの！", "ホマレのプリン", 159, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 61, 3, "たぎってきたの！", "カヤのプリン", 160, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 0, 62, 3, "下剋上、やってやるですなの！", "イノリのプリン", 161, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 63, 3, "どけどけ～！なの～！", "ムイミのプリン", 162, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 64, 3, "ミラーミラー！なの！", "ネネカのプリン", 163, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 65, 3, "プリンに忠誠を誓いますわ！\nなの！", "クレジッタのプリン", 164, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, 1, 66, 3, "ギルド管理協会へようこそ！\nなの！", "カリンのプリン", 165, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの");
