INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ペコリーヌのプリン", 100, 1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 1, 5, "やばいですね☆なの！", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "コッコロのプリン", 101, 1, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 2, 5, "主さま、なの！", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "キャルのプリン", 102, 0, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 3, 5, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "シェフィのプリン", 103, 1, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 4, 5, "おにーたん♪なの！", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "イリヤのプリン", 104, 1, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 5, 5, "ハーハッハッハ！\n世界征服なのじゃなのー！", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "アカリのプリン", 105, 1, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 6, 5, "お兄ちゃんのエッチ！　なの！", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ヨリのプリン", 106, 1, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 7, 5, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "シノブのプリン", 107, 0, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 8, 5, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ジュンのプリン", 108, 1, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 9, 5, "インフェルノシールド！なの！", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "トモのプリン", 109, 1, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 10, 5, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "マツリのプリン", 110, 1, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 11, 5, "タイガーヒーローボンバー！\nなの！", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "クロエのプリン", 111, 1, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 13, 5, "ま、いいんじゃん？\nしらんけどなの", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "チエルのプリン", 112, 1, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 14, 5, "ぷりーん★なの！", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ユニのプリン", 113, 1, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 15, 5, "端的に換言すれば\n青春の味なの？", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "サレンのプリン", 114, 1, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 16, 5, "幼なじみはあんた…なの？", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "スズメのプリン", 115, 1, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 17, 5, "ひゃああんなの～！", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "クルミのプリン", 116, 0, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 18, 5, "サルキデバレヤ！なの！", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "アヤネのプリン", 117, 1, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 19, 5, "プリンフルスイングなの～！", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "マホのプリン", 118, 1, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 20, 5, "みらくるみやりん\nくるりんぱ～☆なの！", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "マコトのプリン", 119, 1, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 21, 5, "ウルフェンバイトなの！", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "カオリのプリン", 120, 1, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 22, 5, "なんくるないさ～！なの！", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "カスミのプリン", 121, 1, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 23, 5, "ふふん。簡単な推理なの", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "マヒルのプリン", 122, 1, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 24, 5, "エリザベス～！なの～！", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "シオリのプリン", 123, 1, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 25, 5, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "リマのプリン", 124, 1, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 26, 5, "もふもふにしてあげるなの！", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "リンのプリン", 125, 0, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 27, 5, "めんどくさいな～なの…", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ラビリスタのプリン", 126, 1, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 28, 5, "プリンの権能を解放するの～！", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "シズルのプリン", 127, 0, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 29, 5, "お姉ちゃんパワー全開！なの！", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "リノのプリン", 128, 1, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 30, 5, "お兄ちゃん、プリンをよこすの！", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ヒヨリのプリン", 129, 1, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 31, 5, "困った時はお互いさまさま！\nなの！", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ユイのプリン", 130, 1, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 32, 5, "フラワリングヒールなの～♪", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "レイのプリン", 131, 1, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 33, 5, "スラッシュテンペスト！なの。", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "アキノのプリン", 132, 1, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 34, 5, "おーほっほほほ！なの！", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ユカリのプリン", 133, 1, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 35, 5, "プリしゅわ最高～！なの！", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "タマキのプリン", 134, 1, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 36, 5, "にゃにゃにゃ！なの！", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ミフユのプリン", 135, 0, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 37, 5, "効率全開！なの！", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ミサトのプリン", 136, 1, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 38, 5, "あらあら～なの～♪", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ハツネのプリン", 137, 1, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 39, 5, "スヤスヤ～…なの…", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "アオイのプリン", 138, 1, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 40, 5, "ぼっちは卒業なの～！", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ノゾミのプリン", 139, 1, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 41, 5, "リズムあげるのー！", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "チカのプリン", 140, 1, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 42, 5, "風の加護を！なの！", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ツムギのプリン", 141, 1, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 43, 5, "騎士さんのために\nしたんじゃありません！なの！", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "クリスティーナのプリン", 142, 0, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 12, 5, "宴の始まりなの！", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ルカのプリン", 143, 1, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 44, 5, "一刀両断、覚悟しな！なの！", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ナナカのプリン", 144, 1, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 45, 5, "きゅぴーん！キタコレ！なの！", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "アンナのプリン", 145, 1, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 46, 5, "人呼んで、疾風のミヤコなの！", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "エリコのプリン", 146, 1, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 47, 5, "クスクス…クスクスクス…なの", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ミツキのプリン", 147, 1, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 48, 5, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "モニカのプリン", 148, 1, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 49, 5, "私は子どもではない！なの！", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ニノンのプリン", 149, 1, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 50, 5, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "クウカのプリン", 150, 0, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 51, 5, "…ぐふふ、じゅるり～なの", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ユキのプリン", 151, 1, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 52, 5, "あぁ、ミヤコはなんて美しいの～", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "アユミのプリン", 152, 0, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 53, 5, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ミソギのプリン", 153, 0, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 54, 5, "にひひ…イタズラの時間なの！", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ミミのプリン", 154, 0, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 55, 5, "ぴょんぴょこぴょーん、なの♪", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "キョウカのプリン", 155, 0, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 56, 5, "へんたいふしんしゃさんなの！", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "スズナのプリン", 156, 1, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 57, 5, "ハート狙い撃ち！なの！", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "イオのプリン", 157, 0, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 58, 5, "授業を始めます♪なの！", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ミサキのプリン", 158, 1, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 59, 5, "立派なレディ、なの！", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ホマレのプリン", 159, 1, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 60, 5, "プリンを持ってこないと\nお仕置きだよ～☆なの！", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "カヤのプリン", 160, 1, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 61, 5, "たぎってきたの！", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "イノリのプリン", 161, 0, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 62, 5, "下剋上、やってやるですなの！", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ムイミのプリン", 162, 1, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 63, 5, "どけどけ～！なの～！", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ネネカのプリン", 163, 1, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 64, 5, "ミラーミラー！なの！", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "クレジッタのプリン", 164, 1, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 65, 5, "プリンに忠誠を誓いますわ！\nなの！", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの");
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "カリンのプリン", 165, 1, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 66, 5, "ギルド管理協会へようこそ！\nなの！", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの");
