INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (1, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "やばいですね☆なの！", "ペコリーヌのプリン", 1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 3, 100, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (2, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "主さま、なの！", "コッコロのプリン", 1, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 3, 101, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (3, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "キャルのプリン", 0, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 3, 102, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (4, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "おにーたん♪なの！", "シェフィのプリン", 1, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 3, 103, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (5, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "ハーハッハッハ！\n世界征服なのじゃなのー！", "イリヤのプリン", 1, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 3, 104, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (6, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "お兄ちゃんのエッチ！　なの！", "アカリのプリン", 1, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 3, 105, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (7, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "ヨリのプリン", 1, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 3, 106, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (8, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "シノブのプリン", 0, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 3, 107, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (9, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "インフェルノシールド！なの！", "ジュンのプリン", 1, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 3, 108, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (10, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "トモのプリン", 1, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 3, 109, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (11, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "タイガーヒーローボンバー！\nなの！", "マツリのプリン", 1, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 3, 110, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (13, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "ま、いいんじゃん？\nしらんけどなの", "クロエのプリン", 1, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 3, 111, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (14, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "ぷりーん★なの！", "チエルのプリン", 1, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 3, 112, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (15, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "端的に換言すれば\n青春の味なの？", "ユニのプリン", 1, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 3, 113, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (16, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "幼なじみはあんた…なの？", "サレンのプリン", 1, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 3, 114, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (17, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "ひゃああんなの～！", "スズメのプリン", 1, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 3, 115, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (18, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "サルキデバレヤ！なの！", "クルミのプリン", 0, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 3, 116, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (19, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "プリンフルスイングなの～！", "アヤネのプリン", 1, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 3, 117, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (20, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "みらくるみやりん\nくるりんぱ～☆なの！", "マホのプリン", 1, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 3, 118, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (21, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "ウルフェンバイトなの！", "マコトのプリン", 1, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 3, 119, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (22, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "なんくるないさ～！なの！", "カオリのプリン", 1, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 3, 120, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (23, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "ふふん。簡単な推理なの", "カスミのプリン", 1, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 3, 121, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (24, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "エリザベス～！なの～！", "マヒルのプリン", 1, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 3, 122, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (25, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "シオリのプリン", 1, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 3, 123, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (26, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "もふもふにしてあげるなの！", "リマのプリン", 1, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 3, 124, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (27, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "めんどくさいな～なの…", "リンのプリン", 0, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 3, 125, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (28, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "プリンの権能を解放するの～！", "ラビリスタのプリン", 1, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 3, 126, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (29, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "お姉ちゃんパワー全開！なの！", "シズルのプリン", 0, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 3, 127, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (30, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "お兄ちゃん、プリンをよこすの！", "リノのプリン", 1, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 3, 128, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (31, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "困った時はお互いさまさま！\nなの！", "ヒヨリのプリン", 1, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 3, 129, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (32, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "フラワリングヒールなの～♪", "ユイのプリン", 1, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 3, 130, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (33, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "スラッシュテンペスト！なの。", "レイのプリン", 1, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 3, 131, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (34, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "おーほっほほほ！なの！", "アキノのプリン", 1, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 3, 132, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (35, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "プリしゅわ最高～！なの！", "ユカリのプリン", 1, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 3, 133, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (36, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "にゃにゃにゃ！なの！", "タマキのプリン", 1, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 3, 134, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (37, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "効率全開！なの！", "ミフユのプリン", 0, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 3, 135, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (38, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "あらあら～なの～♪", "ミサトのプリン", 1, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 3, 136, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (39, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "スヤスヤ～…なの…", "ハツネのプリン", 1, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 3, 137, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (40, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "ぼっちは卒業なの～！", "アオイのプリン", 1, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 3, 138, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (41, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "リズムあげるのー！", "ノゾミのプリン", 1, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 3, 139, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (42, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "風の加護を！なの！", "チカのプリン", 1, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 3, 140, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (43, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "騎士さんのために\nしたんじゃありません！なの！", "ツムギのプリン", 1, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 3, 141, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (12, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "宴の始まりなの！", "クリスティーナのプリン", 0, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 3, 142, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (44, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "一刀両断、覚悟しな！なの！", "ルカのプリン", 1, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 3, 143, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (45, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "きゅぴーん！キタコレ！なの！", "ナナカのプリン", 1, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 3, 144, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (46, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "人呼んで、疾風のミヤコなの！", "アンナのプリン", 1, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 3, 145, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (47, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "クスクス…クスクスクス…なの", "エリコのプリン", 1, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 3, 146, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (48, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "ミツキのプリン", 1, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 3, 147, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (49, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "私は子どもではない！なの！", "モニカのプリン", 1, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 3, 148, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (50, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "ニノンのプリン", 1, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 3, 149, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (51, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "…ぐふふ、じゅるり～なの", "クウカのプリン", 0, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 3, 150, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (52, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "あぁ、ミヤコはなんて美しいの～", "ユキのプリン", 1, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 3, 151, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (53, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "アユミのプリン", 0, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 3, 152, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (54, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "にひひ…イタズラの時間なの！", "ミソギのプリン", 0, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 3, 153, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (55, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ぴょんぴょこぴょーん、なの♪", "ミミのプリン", 0, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 3, 154, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (56, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "へんたいふしんしゃさんなの！", "キョウカのプリン", 0, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 3, 155, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (57, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "ハート狙い撃ち！なの！", "スズナのプリン", 1, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 3, 156, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (58, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "授業を始めます♪なの！", "イオのプリン", 0, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 3, 157, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (59, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "立派なレディ、なの！", "ミサキのプリン", 1, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 3, 158, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (60, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", "ホマレのプリン", 1, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 3, 159, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (61, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "たぎってきたの！", "カヤのプリン", 1, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 3, 160, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (62, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "下剋上、やってやるですなの！", "イノリのプリン", 0, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 3, 161, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (63, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "どけどけ～！なの～！", "ムイミのプリン", 1, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 3, 162, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (64, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ミラーミラー！なの！", "ネネカのプリン", 1, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 3, 163, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (65, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "プリンに忠誠を誓いますわ！\nなの！", "クレジッタのプリン", 1, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 3, 164, 5);
INSERT INTO `v1_9104f46ab57498e61748f8690b1cd9229135d1dca4fe1db46be169422e72704c` VALUES (66, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "ギルド管理協会へようこそ！\nなの！", "カリンのプリン", 1, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 3, 165, 5);
