INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1000001, "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？", "2030-04-01 14:00:00", 190801, 13, 1000, "2020-02-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1000002, "楽しいミニゲームをご紹介！\nあなたはどのミニゲームで\n遊びますか？", "2030-04-01 14:00:00", 190801, 13, 1000, "2020-02-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1001001, "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！", "2030-04-01 14:00:00", 190801, 13, 1001, "2020-02-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1002001, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！", "2030-04-01 14:00:00", 190801, 12, 1002, "2020-02-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1003001, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", "2030-04-01 14:00:00", 190801, 11, 1003, "2020-02-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1004001, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", "2030-04-01 14:00:00", 190801, 3, 1004, "2020-02-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1005001, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", "2030-04-01 14:00:00", 190801, 7, 1005, "2020-02-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1006001, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", "2030-04-01 14:00:00", 190801, 2, 1006, "2020-05-31 12:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1007001, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう", "2030-04-01 14:00:00", 190801, 4, 1007, "2020-08-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1008001, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n", "2030-04-01 14:00:00", 190801, 6, 1008, "2021-08-31 12:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1009001, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。", "2030-04-01 14:00:00", 190801, 13, 1009, "2021-12-18 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1010001, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", "2030-04-01 14:00:00", 190801, 4, 1010, "2022-02-15 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1011001, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", "2030-04-01 14:00:00", 190801, 6, 1011, "2022-07-18 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1012001, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪", "2030-04-01 14:00:00", 190801, 6, 1012, "2023-09-18 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1013001, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪", "2030-04-01 14:00:00", 190801, 6, 1013, "2024-04-18 15:00:00");
INSERT INTO `v1_98146be4ae3f76af359b5cf840466e2db80f1c2d170de0f3dd93585f188c34c6` VALUES (0, 1014001, "バンディ・シスターズのみなさん\nと戦車バトルに挑戦です！\nパーツやメンバーの組み合わせで\nマシンの特徴が変化しますよ♪", "2030-04-01 14:00:00", 190801, 6, 1014, "2024-11-18 15:00:00");
