INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (6, -285.0, 15.5, 0.0, 61, 301, "2030/01/01 14:59:59", 133801, "2025/01/31 12:00:00", "おや、私の力が必要……と？\nグレゴールが手伝うそうですよ\n私は戦わないのか、という顔ですね\n……考えておきます", 1020103, 1, "ネネカ", 10201);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (2, -285.0, 9.061, 0.0, 61, 101, "2030/01/01 14:59:59", 134301, "2025/02/28 12:00:00", "わたしだけの歌…\n一緒に、探してくれる…？\nあなたがいれば…\nもっとがんばれるから…", 1020201, 5, "ランファ", 10202);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 7.699, 0.0, 61, 201, "2030/01/01 14:59:59", 134401, "2025/02/28 12:00:00", "同じ唱喚士として、仲間として\n私にできることをします\n…これも、あなたが\n背中を押してくれたおかげですよ", 1020202, 2, "チカ", 10202);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 8.595, 0.0, 61, 101, "2030/01/01 14:59:59", 134601, "2025/03/31 12:00:00", "世界の平和が私とクローチェさんに\nかかってるんですよね？\nぼっちには過大すぎる期待ですけど\nふ、二人でなら背負ってみせます！", 1020301, 2, "アオイ", 10203);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 7.758, 0.0, 61, 201, "2030/01/01 14:59:59", 134501, "2025/03/31 12:00:00", "新しく手に入れたこの翼で…\nナイトさんがどこにいたって\nわたし、駆けつけちゃいますよっ！\nクローチェ・エアリアル発進ですっ", 1020302, 2, "クローチェ", 10203);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (2, -285.0, 7.0, 0.0, 61, 101, "2030/01/01 14:59:59", 132302, "2025/04/30 12:00:00", "今日はダーリンとの初デート♪\n二人でショッピングして、その後は\n…って私、指名手配されてる！？\nちょ、た…助けてダーリン～！！", 1020401, 6, "ルイズマリー", 10204);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 7.904, 0.0, 61, 201, "2030/01/01 14:59:59", 132402, "2025/04/30 12:00:00", "ルイちゃんのおばあちゃん優しくて\nいつもにこにこで、イロハ大好き♪\nでも一番好きなのは、角がすっごく\n大きくてかっこいいとこなんだ～♪", 1020402, 2, "イロハ", 10204);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (4, -285.0, 8.487, 0.0, 61, 301, "2030/01/01 14:59:59", 132502, "2025/04/30 12:00:00", "ノゾミちゃんのおかげで売上が安定\nしてきたのに、臨時休業、痛すぎる\n…ま、ルイちゃんのために\n一肌脱ぐか。行くよイロハ～", 1020403, 2, "ソノ", 10204);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 8.763, 0.0, 61, 101, "2030/01/01 14:59:59", 134901, "2025/05/31 12:00:00", "ヲ～ッホッホ♪\nあたくし様と同じお空気を吸える\nお幸運を噛みしめなさいですわ～♪\nヲ～ッホッホッホ～♪", 1020502, 2, "カノン", 10205);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 6.417, 0.0, 61, 201, "2030/01/01 14:59:59", 134801, "2025/05/31 12:00:00", "いきますよ騎士さん！\nカノンさんと力を合わせて…力を…\n…本当にあの人なんなんです？\nこれまでになく不安なんですけど！", 1020503, 3, "ツムギ", 10205);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (3, -285.0, 10.335, 0.0, 61, 101, "2030/01/01 14:59:59", 135001, "2025/06/30 12:00:00", "『願いが叶う海岸』か…怪しい\nだというのに、阿賀斗紫布菜たちは\n呑気に戯れている…見ていられない\n騎士クンは、わたしが守ってみせる", 1020601, 7, "エリス", 10206);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (2, -285.0, 6.281, 0.0, 61, 201, "2030/01/01 14:59:59", 135101, "2025/06/30 12:00:00", "せっかくの夏休みなんだし\nたまにはビーチでゆっくりしましょ\n…え、もしかしてこの夏もあんまり\n休めなかったりする感じなの？", 1020602, 4, "シェフィ", 10206);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 12.244, 0.0, 61, 301, "2030/01/01 14:59:59", 135201, "2025/06/30 12:00:00", "いっぱい。たべて\nおおきく。なると\nせーぎ。まもれる。はず\nお兄。いっしょに。スイカ。たべよ", 1020603, 2, "プレシア", 10206);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (2, -285.0, 6.339, 0.0, 61, 101, "2030/01/01 14:59:59", 135301, "2025/07/31 12:00:00", "ひと夏の冒険のはじまりです！\nアネモネさんも楽しんでくれたら\n…できれば、もう少し\n仲良くなれたらうれしいな…", 1020701, 8, "クリア", 10207);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (7, -285.0, 8.184, 0.0, 61, 201, "2030/01/01 14:59:59", 135401, "2025/07/31 12:00:00", "─幻の花…\n本当にあるのか謎─ですけど\n探すくらいは、\n付き合ってもいいです", 1020702, 9, "アネモネ", 10207);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (9, -285.0, 3.906, 0.0, 61, 101, "2030/01/01 14:59:59", 129101, "2025/08/31 12:00:00", "あははっ！　笑う門には福来る！\n笑ってればきっと、複製世界にも\n未来はやってくるって信じてる！\nそうだよね、シナツちゃん！", 1020801, 2, "アイラ", 10208);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 14.465, 0.0, 61, 201, "2030/01/01 14:59:59", 129201, "2025/08/31 12:00:00", "繰り返されるリセットの先の未来\n何度願っても手に入らなかった未来\n…でも、私は諦めない。絶対にだ\nキミも同じだろ…アイラ？", 1020802, 2, "シナツ", 10208);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (4, -285.0, 8.662, 0.0, 61, 301, "2030/01/01 14:59:59", 100101, "2025/08/31 12:00:00", "アイラの世界のみんなも、\n一生懸命生きてるんだ…！\n…なのに、世界の危機だなんて！\nあたし、絶対、絶対力になるよ！", 1020803, 3, "ヒヨリ", 10208);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 10.915, 0.0, 61, 401, "2030/01/01 14:59:59", 100301, "2025/08/31 12:00:00", "シナツ…もう一人の私\n彼女が大切にしている世界を\n絶対に終わらせはしない…！\n彼女と笑い合える未来のために！", 1020804, 3, "レイ", 10208);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 6.042, 0.0, 61, 101, "2030/01/01 14:59:59", 135802, "2025/09/30 12:00:00", "あ、ど、ども…\nあ、あんま見ねぇでくれっと\nその、恥ずかしくて…\n体が熱くなって、溶けちまう…", 1020901, 7, "ユキノ", 10209);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (4, -285.0, 9.402, 0.0, 61, 201, "2030/01/01 14:59:59", 135903, "2025/09/30 12:00:00", "なんで俺が幽霊に…意味不明だ\nこれじゃ死んでも死にきれねェ\n悪ィがこれも何かの縁だ、\nよろしく頼むぜ騎士公", 1020902, 1, "サツキ", 10209);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (8, -285.0, 8.16, 0.0, 61, 301, "2030/01/01 14:59:59", 136902, "2025/09/30 12:00:00", "う、うじゅる…\nサツキ姉しゃまを助けるためにも\nまずは、腹ごしらえでしゅね…！\nタコ焼き、召し上がってくだしゃい", 1020903, 1, "ルルィ", 10209);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (3, -285.0, 5.0, 0.0, 61, 101, "2030/01/01 14:59:59", 137401, "2025/10/31 12:00:00", "ひさしぶりのジオ・ゲヘナ\n歓迎パーティーで起きた殺人事件！\nランドソルの名探偵の名にかけて\n解き明かしてみせようじゃないか！", 1021001, 2, "カスミ", 10210);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (4, -285.0, 8.12, 0.0, 61, 201, "2030/01/01 14:59:59", 136001, "2025/10/31 12:00:00", "…わたしはどこの誰なのでしょう\nどこから来てどこへ行くのでしょう\nこの事件の真相が分かれば、\n何かが掴めるような気がいたします", 1021002, 1, "アラクネ", 10210);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (4, -285.0, 6.386, 0.0, 61, 301, "2030/01/01 14:59:59", 136202, "2025/10/31 12:00:00", "ナーナ、すいりとかろんぱとか、\nむずかしくてわかんないけど…\nお友だちとドーナツのためならっ\nせーいっぱいがんばるよっ！", 1021003, 2, "ナーナ", 10210);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (3, -285.0, 7.438, 0.0, 61, 101, "2030/01/01 14:59:59", 136402, "2025/11/30 12:00:00", "騎士クンに想いを伝えたい…だから\nミソラちゃん、わたしがんばるね！\n…あれ？　わたし、なんでこんなに\n必死になってるんだろう…？", 1021101, 4, "ユイ", 10211);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 8.35, 0.0, 61, 201, "2030/01/01 14:59:59", 136302, "2025/11/30 12:00:00", "騎士さんとのデートはわたしに\nお任せです★　なのでユイさんは\n安心して計画に専念して…たくさん\nドキドキしちゃってくださいね", 1021102, 5, "ミソラ", 10211);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 5.0, 0.0, 61, 101, "2030/01/01 14:59:59", 136501, "2025/12/31 12:00:00", "新しい年がやってくるね！\nお正月の料理も飾りつけも、\nお姉ちゃんにお任せだよ♪\nみんなで楽しく新年を迎えよう！", 1021201, 2, "シズル", 10212);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 5.25, 0.0, 61, 201, "2030/01/01 14:59:59", 137902, "2025/12/31 12:00:00", "おうちの掃除もトラブルも、 \n世界を揺るがす大ピンチも！\n今年のことは今年のうちに \nサッサと片付けちゃいますっ！", 1021202, 2, "リノ", 10212);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 7.5, 0.0, 61, 301, "2030/01/01 14:59:59", 106801, "2025/12/31 12:00:00", "年の瀬はギルドマスターも\nつい駆け回っちゃう忙しい時期だ！\n一年いろいろあったけど\nお互いイイ年を迎えよう、少年！", 1021203, 2, "ラビリスタ", 10212);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 8.204, 0.0, 61, 101, "2030/01/01 14:59:59", 126501, "2026/01/31 12:00:00", "漂う異国の料理の香り…それだけで\n世界中を旅した気分に浸れますね\nふふ、ほらクローチェ\nあまりはしゃぎすぎないように…♪", 1021301, 2, "ライラエル", 10213);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 7.176, 0.0, 61, 201, "2030/01/01 14:59:59", 129701, "2026/01/31 12:00:00", "異界の姫君と煮炊きにて競い合う…\n感奮興起、いとをかし\n華やかなる祭典と相成るよう、一肌\n脱がせていただきとう存じまする", 1021302, 2, "ネフィ＝ネラ", 10213);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 8.602, 0.0, 61, 301, "2030/01/01 14:59:59", 133101, "2026/01/31 12:00:00", "『ビストロプリンセス』のために\n料理の特訓を重ねてまいりました\nこの料理で騎士様の…い、いえ\n皆様の心とお腹を満たしますわ！", 1021303, 5, "ヴァイオレット", 10213);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, -285.0, 10.14, 0.0, 61, 102, "2030/01/01 14:59:59", 138101, "2026/02/06 12:00:00", "多様な味わいが織りなすハーモニー\nそれこそが我が国の矜持です\nもしもそれを乱すようであれば\nわたしも優しくはありませんよ？", 1021304, 3, "ライラエル", 10213);
