INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, "ミッションをクリアして、\n報酬をゲットしましょう！", 0.0, "2030/01/01 14:59:59", 2, 1, "カリン", 2, 1000002, 3.099, "2025/01/31 12:00:00", 62, -416.5, 190801);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, "受け取ることができる\n報酬があるようです\n確認してみてください", 0.0, "2030/01/01 14:59:59", 2, 1, "カリン", 3, 1000003, 2.801, "2025/01/31 12:00:00", 63, -416.5, 190801);
