INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, "ギルド管理協会です！\nイベント報酬券をお持ちでしたら、様々なアイテムと交換できますよ", 0.0, "2030/01/01 14:59:59", 1, 2, "カリン", 5, 1000005, 2.12, "2025/01/31 12:00:00", 64, -363.0, 190801);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, "お疲れさまです！\nこちらでイベント報酬券とアイテムを交換することができますよ", 0.0, "2030/01/01 14:59:59", 2, 1, "カリン", 6, 1000006, 1.68, "2025/01/31 12:00:00", 64, -363.0, 190801);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (1, "いつもありがとうございます！\n報酬交換ですか？\nでは、イベント報酬券とお引き換えいたしますね", 0.0, "2030/01/01 14:59:59", 1, 2, "カリン", 7, 1000007, 2.33, "2025/01/31 12:00:00", 64, -363.0, 190801);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10201, "今度の敵は、アタシにとっても\n放ってはおけない存在だ\n少年、アタシも全力で行くからさ\n一緒に守ろう、この世界を", -6.0, "2030/01/01 14:59:59", 7, 3, "ラビリスタ", 101, 1020101, 5.18, "2025/01/31 12:00:00", 61, -285.0, 133701);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10201, "退屈の吹き飛ぶような\n戦いの気配がするなぁ坊や\nさっさとワタシをそこへ連れてゆけ\n世界も守ってやるさ、ついでにな", -2.0, "2030/01/01 14:59:59", 2, 1, "クリスティーナ", 201, 1020102, 8.77, "2025/01/31 12:00:00", 61, -285.0, 133901);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10201, "おや、私の力が必要……と？\nグレゴールが手伝うそうですよ\n私は戦わないのか、という顔ですね\n……考えておきます", 0.0, "2030/01/01 14:59:59", 1, 6, "ネネカ", 301, 1020103, 15.5, "2025/01/31 12:00:00", 61, -285.0, 133801);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10202, "わたしだけの歌…\n一緒に、探してくれる…？\nあなたがいれば…\nもっとがんばれるから…", 0.0, "2030/01/01 14:59:59", 5, 2, "ランファ", 101, 1020201, 9.061, "2025/02/28 12:00:00", 61, -285.0, 134301);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10202, "同じ唱喚士として、仲間として\n私にできることをします\n…これも、あなたが\n背中を押してくれたおかげですよ", 0.0, "2030/01/01 14:59:59", 2, 1, "チカ", 201, 1020202, 7.699, "2025/02/28 12:00:00", 61, -285.0, 134401);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10203, "世界の平和が私とクローチェさんに\nかかってるんですよね？\nぼっちには過大すぎる期待ですけど\nふ、二人でなら背負ってみせます！", 0.0, "2030/01/01 14:59:59", 2, 1, "アオイ", 101, 1020301, 8.595, "2025/03/31 12:00:00", 61, -285.0, 134601);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10203, "新しく手に入れたこの翼で…\nナイトさんがどこにいたって\nわたし、駆けつけちゃいますよっ！\nクローチェ・エアリアル発進ですっ", 0.0, "2030/01/01 14:59:59", 2, 1, "クローチェ", 201, 1020302, 7.758, "2025/03/31 12:00:00", 61, -285.0, 134501);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10204, "今日はダーリンとの初デート♪\n二人でショッピングして、その後は\n…って私、指名手配されてる！？\nちょ、た…助けてダーリン～！！", 0.0, "2030/01/01 14:59:59", 6, 2, "ルイズマリー", 101, 1020401, 7.0, "2025/04/30 12:00:00", 61, -285.0, 132302);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10204, "ルイちゃんのおばあちゃん優しくて\nいつもにこにこで、イロハ大好き♪\nでも一番好きなのは、角がすっごく\n大きくてかっこいいとこなんだ～♪", 0.0, "2030/01/01 14:59:59", 2, 1, "イロハ", 201, 1020402, 7.904, "2025/04/30 12:00:00", 61, -285.0, 132402);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10204, "ノゾミちゃんのおかげで売上が安定\nしてきたのに、臨時休業、痛すぎる\n…ま、ルイちゃんのために\n一肌脱ぐか。行くよイロハ～", 0.0, "2030/01/01 14:59:59", 2, 4, "ソノ", 301, 1020403, 8.487, "2025/04/30 12:00:00", 61, -285.0, 132502);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10205, "ヲ～ッホッホ♪\nあたくし様と同じお空気を吸える\nお幸運を噛みしめなさいですわ～♪\nヲ～ッホッホッホ～♪", 0.0, "2030/01/01 14:59:59", 2, 1, "カノン", 101, 1020502, 8.763, "2025/05/31 12:00:00", 61, -285.0, 134901);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10205, "いきますよ騎士さん！\nカノンさんと力を合わせて…力を…\n…本当にあの人なんなんです？\nこれまでになく不安なんですけど！", 0.0, "2030/01/01 14:59:59", 3, 1, "ツムギ", 201, 1020503, 6.417, "2025/05/31 12:00:00", 61, -285.0, 134801);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10206, "『願いが叶う海岸』か…怪しい\nだというのに、阿賀斗紫布菜たちは\n呑気に戯れている…見ていられない\n騎士クンは、わたしが守ってみせる", 0.0, "2030/01/01 14:59:59", 7, 3, "エリス", 101, 1020601, 10.335, "2025/06/30 12:00:00", 61, -285.0, 135001);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10206, "せっかくの夏休みなんだし\nたまにはビーチでゆっくりしましょ\n…え、もしかしてこの夏もあんまり\n休めなかったりする感じなの？", 0.0, "2030/01/01 14:59:59", 4, 2, "シェフィ", 201, 1020602, 6.281, "2025/06/30 12:00:00", 61, -285.0, 135101);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10206, "いっぱい。たべて\nおおきく。なると\nせーぎ。まもれる。はず\nお兄。いっしょに。スイカ。たべよ", 0.0, "2030/01/01 14:59:59", 2, 1, "プレシア", 301, 1020603, 12.244, "2025/06/30 12:00:00", 61, -285.0, 135201);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10207, "ひと夏の冒険のはじまりです！\nアネモネさんも楽しんでくれたら\n…できれば、もう少し\n仲良くなれたらうれしいな…", 0.0, "2030/01/01 14:59:59", 8, 2, "クリア", 101, 1020701, 6.339, "2025/07/31 12:00:00", 61, -285.0, 135301);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10207, "─幻の花…\n本当にあるのか謎─ですけど\n探すくらいは、\n付き合ってもいいです", 0.0, "2030/01/01 14:59:59", 9, 7, "アネモネ", 201, 1020702, 8.184, "2025/07/31 12:00:00", 61, -285.0, 135401);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10208, "あははっ！　笑う門には福来る！\n笑ってればきっと、複製世界にも\n未来はやってくるって信じてる！\nそうだよね、シナツちゃん！", 0.0, "2030/01/01 14:59:59", 2, 9, "アイラ", 101, 1020801, 3.906, "2025/08/31 12:00:00", 61, -285.0, 129101);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10208, "繰り返されるリセットの先の未来\n何度願っても手に入らなかった未来\n…でも、私は諦めない。絶対にだ\nキミも同じだろ…アイラ？", 0.0, "2030/01/01 14:59:59", 2, 1, "シナツ", 201, 1020802, 14.465, "2025/08/31 12:00:00", 61, -285.0, 129201);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10208, "アイラの世界のみんなも、\n一生懸命生きてるんだ…！\n…なのに、世界の危機だなんて！\nあたし、絶対、絶対力になるよ！", 0.0, "2030/01/01 14:59:59", 3, 4, "ヒヨリ", 301, 1020803, 8.662, "2025/08/31 12:00:00", 61, -285.0, 100101);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10208, "シナツ…もう一人の私\n彼女が大切にしている世界を\n絶対に終わらせはしない…！\n彼女と笑い合える未来のために！", 0.0, "2030/01/01 14:59:59", 3, 1, "レイ", 401, 1020804, 10.915, "2025/08/31 12:00:00", 61, -285.0, 100301);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10209, "あ、ど、ども…\nあ、あんま見ねぇでくれっと\nその、恥ずかしくて…\n体が熱くなって、溶けちまう…", 0.0, "2030/01/01 14:59:59", 7, 1, "ユキノ", 101, 1020901, 6.042, "2025/09/30 12:00:00", 61, -285.0, 135802);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10209, "なんで俺が幽霊に…意味不明だ\nこれじゃ死んでも死にきれねェ\n悪ィがこれも何かの縁だ、\nよろしく頼むぜ騎士公", 0.0, "2030/01/01 14:59:59", 1, 4, "サツキ", 201, 1020902, 9.402, "2025/09/30 12:00:00", 61, -285.0, 135903);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10209, "う、うじゅる…\nサツキ姉しゃまを助けるためにも\nまずは、腹ごしらえでしゅね…！\nタコ焼き、召し上がってくだしゃい", 0.0, "2030/01/01 14:59:59", 1, 8, "ルルィ", 301, 1020903, 8.16, "2025/09/30 12:00:00", 61, -285.0, 136902);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10210, "ひさしぶりのジオ・ゲヘナ\n歓迎パーティーで起きた殺人事件！\nランドソルの名探偵の名にかけて\n解き明かしてみせようじゃないか！", 0.0, "2030/01/01 14:59:59", 2, 3, "カスミ", 101, 1021001, 5.0, "2025/10/31 12:00:00", 61, -285.0, 137401);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10210, "…わたしはどこの誰なのでしょう\nどこから来てどこへ行くのでしょう\nこの事件の真相が分かれば、\n何かが掴めるような気がいたします", 0.0, "2030/01/01 14:59:59", 1, 4, "アラクネ", 201, 1021002, 8.12, "2025/10/31 12:00:00", 61, -285.0, 136001);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10210, "ナーナ、すいりとかろんぱとか、\nむずかしくてわかんないけど…\nお友だちとドーナツのためならっ\nせーいっぱいがんばるよっ！", 0.0, "2030/01/01 14:59:59", 2, 4, "ナーナ", 301, 1021003, 6.386, "2025/10/31 12:00:00", 61, -285.0, 136202);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10211, "騎士クンに想いを伝えたい…だから\nミソラちゃん、わたしがんばるね！\n…あれ？　わたし、なんでこんなに\n必死になってるんだろう…？", 0.0, "2030/01/01 14:59:59", 4, 3, "ユイ", 101, 1021101, 7.438, "2025/11/30 12:00:00", 61, -285.0, 136402);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10211, "騎士さんとのデートはわたしに\nお任せです★　なのでユイさんは\n安心して計画に専念して…たくさん\nドキドキしちゃってくださいね", 0.0, "2030/01/01 14:59:59", 5, 1, "ミソラ", 201, 1021102, 8.35, "2025/11/30 12:00:00", 61, -285.0, 136302);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10212, "新しい年がやってくるね！\nお正月の料理も飾りつけも、\nお姉ちゃんにお任せだよ♪\nみんなで楽しく新年を迎えよう！", 0.0, "2030/01/01 14:59:59", 2, 1, "シズル", 101, 1021201, 5.0, "2025/12/31 12:00:00", 61, -285.0, 136501);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10212, "おうちの掃除もトラブルも、 \n世界を揺るがす大ピンチも！\n今年のことは今年のうちに \nサッサと片付けちゃいますっ！", 0.0, "2030/01/01 14:59:59", 2, 1, "リノ", 201, 1021202, 5.25, "2025/12/31 12:00:00", 61, -285.0, 137902);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10212, "年の瀬はギルドマスターも\nつい駆け回っちゃう忙しい時期だ！\n一年いろいろあったけど\nお互いイイ年を迎えよう、少年！", 0.0, "2030/01/01 14:59:59", 2, 1, "ラビリスタ", 301, 1021203, 7.5, "2025/12/31 12:00:00", 61, -285.0, 106801);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10213, "漂う異国の料理の香り…それだけで\n世界中を旅した気分に浸れますね\nふふ、ほらクローチェ\nあまりはしゃぎすぎないように…♪", 0.0, "2030/01/01 14:59:59", 2, 1, "ライラエル", 101, 1021301, 8.204, "2026/01/31 12:00:00", 61, -285.0, 126501);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10213, "異界の姫君と煮炊きにて競い合う…\n感奮興起、いとをかし\n華やかなる祭典と相成るよう、一肌\n脱がせていただきとう存じまする", 0.0, "2030/01/01 14:59:59", 2, 1, "ネフィ＝ネラ", 201, 1021302, 7.176, "2026/01/31 12:00:00", 61, -285.0, 129701);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10213, "『ビストロプリンセス』のために\n料理の特訓を重ねてまいりました\nこの料理で騎士様の…い、いえ\n皆様の心とお腹を満たしますわ！", 0.0, "2030/01/01 14:59:59", 5, 1, "ヴァイオレット", 301, 1021303, 8.602, "2026/01/31 12:00:00", 61, -285.0, 133101);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10213, "多様な味わいが織りなすハーモニー\nそれこそが我が国の矜持です\nもしもそれを乱すようであれば\nわたしも優しくはありませんよ？", 0.0, "2030/01/01 14:59:59", 3, 1, "ライラエル", 102, 1021304, 10.14, "2026/02/06 12:00:00", 61, -285.0, 138101);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10213, "煉獄の穢土にてひと際輝く民草の心\n此れを守護りまするがわっちの責務\n如何な困難待ち受けようと\n不退転の矜持に揺らぎはありんせん", 0.0, "2030/01/01 14:59:59", 7, 1, "ネフィ＝ネラ", 202, 1021305, 10.152, "2026/02/06 12:00:00", 61, -285.0, 138201);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (10213, "裏世界の皆様は危機を脱し、\n残るはランドソルの皆様のみ…\n彼らを救い出すために、どうか…\nお立ち下さいペコリーヌ姫…！", 0.0, "2030/01/01 14:59:59", 3, 4, "ヴァイオレット", 302, 1021306, 9.688, "2026/02/06 12:00:00", 61, -285.0, 138001);
