INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10201, 1020101, 101, "今度の敵は、アタシにとっても\n放ってはおけない存在だ\n少年、アタシも全力で行くからさ\n一緒に守ろう、この世界を", "2025/01/31 12:00:00", 3, 7, "2030/01/01 14:59:59", "ラビリスタ", 133701, -285.0, -6.0, 5.18);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10201, 1020102, 201, "退屈の吹き飛ぶような\n戦いの気配がするなぁ坊や\nさっさとワタシをそこへ連れてゆけ\n世界も守ってやるさ、ついでにな", "2025/01/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "クリスティーナ", 133901, -285.0, -2.0, 8.77);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10201, 1020103, 301, "おや、私の力が必要……と？\nグレゴールが手伝うそうですよ\n私は戦わないのか、という顔ですね\n……考えておきます", "2025/01/31 12:00:00", 6, 1, "2030/01/01 14:59:59", "ネネカ", 133801, -285.0, 0.0, 15.5);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10202, 1020201, 101, "わたしだけの歌…\n一緒に、探してくれる…？\nあなたがいれば…\nもっとがんばれるから…", "2025/02/28 12:00:00", 2, 5, "2030/01/01 14:59:59", "ランファ", 134301, -285.0, 0.0, 9.061);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10202, 1020202, 201, "同じ唱喚士として、仲間として\n私にできることをします\n…これも、あなたが\n背中を押してくれたおかげですよ", "2025/02/28 12:00:00", 1, 2, "2030/01/01 14:59:59", "チカ", 134401, -285.0, 0.0, 7.699);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10203, 1020301, 101, "世界の平和が私とクローチェさんに\nかかってるんですよね？\nぼっちには過大すぎる期待ですけど\nふ、二人でなら背負ってみせます！", "2025/03/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "アオイ", 134601, -285.0, 0.0, 8.595);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10203, 1020302, 201, "新しく手に入れたこの翼で…\nナイトさんがどこにいたって\nわたし、駆けつけちゃいますよっ！\nクローチェ・エアリアル発進ですっ", "2025/03/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "クローチェ", 134501, -285.0, 0.0, 7.758);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10204, 1020401, 101, "今日はダーリンとの初デート♪\n二人でショッピングして、その後は\n…って私、指名手配されてる！？\nちょ、た…助けてダーリン～！！", "2025/04/30 12:00:00", 2, 6, "2030/01/01 14:59:59", "ルイズマリー", 132302, -285.0, 0.0, 7.0);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10204, 1020402, 201, "ルイちゃんのおばあちゃん優しくて\nいつもにこにこで、イロハ大好き♪\nでも一番好きなのは、角がすっごく\n大きくてかっこいいとこなんだ～♪", "2025/04/30 12:00:00", 1, 2, "2030/01/01 14:59:59", "イロハ", 132402, -285.0, 0.0, 7.904);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10204, 1020403, 301, "ノゾミちゃんのおかげで売上が安定\nしてきたのに、臨時休業、痛すぎる\n…ま、ルイちゃんのために\n一肌脱ぐか。行くよイロハ～", "2025/04/30 12:00:00", 4, 2, "2030/01/01 14:59:59", "ソノ", 132502, -285.0, 0.0, 8.487);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10205, 1020502, 101, "ヲ～ッホッホ♪\nあたくし様と同じお空気を吸える\nお幸運を噛みしめなさいですわ～♪\nヲ～ッホッホッホ～♪", "2025/05/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "カノン", 134901, -285.0, 0.0, 8.763);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10205, 1020503, 201, "いきますよ騎士さん！\nカノンさんと力を合わせて…力を…\n…本当にあの人なんなんです？\nこれまでになく不安なんですけど！", "2025/05/31 12:00:00", 1, 3, "2030/01/01 14:59:59", "ツムギ", 134801, -285.0, 0.0, 6.417);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10206, 1020601, 101, "『願いが叶う海岸』か…怪しい\nだというのに、阿賀斗紫布菜たちは\n呑気に戯れている…見ていられない\n騎士クンは、わたしが守ってみせる", "2025/06/30 12:00:00", 3, 7, "2030/01/01 14:59:59", "エリス", 135001, -285.0, 0.0, 10.335);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10206, 1020602, 201, "せっかくの夏休みなんだし\nたまにはビーチでゆっくりしましょ\n…え、もしかしてこの夏もあんまり\n休めなかったりする感じなの？", "2025/06/30 12:00:00", 2, 4, "2030/01/01 14:59:59", "シェフィ", 135101, -285.0, 0.0, 6.281);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10206, 1020603, 301, "いっぱい。たべて\nおおきく。なると\nせーぎ。まもれる。はず\nお兄。いっしょに。スイカ。たべよ", "2025/06/30 12:00:00", 1, 2, "2030/01/01 14:59:59", "プレシア", 135201, -285.0, 0.0, 12.244);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10207, 1020701, 101, "ひと夏の冒険のはじまりです！\nアネモネさんも楽しんでくれたら\n…できれば、もう少し\n仲良くなれたらうれしいな…", "2025/07/31 12:00:00", 2, 8, "2030/01/01 14:59:59", "クリア", 135301, -285.0, 0.0, 6.339);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10207, 1020702, 201, "─幻の花…\n本当にあるのか謎─ですけど\n探すくらいは、\n付き合ってもいいです", "2025/07/31 12:00:00", 7, 9, "2030/01/01 14:59:59", "アネモネ", 135401, -285.0, 0.0, 8.184);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10208, 1020801, 101, "あははっ！　笑う門には福来る！\n笑ってればきっと、複製世界にも\n未来はやってくるって信じてる！\nそうだよね、シナツちゃん！", "2025/08/31 12:00:00", 9, 2, "2030/01/01 14:59:59", "アイラ", 129101, -285.0, 0.0, 3.906);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10208, 1020802, 201, "繰り返されるリセットの先の未来\n何度願っても手に入らなかった未来\n…でも、私は諦めない。絶対にだ\nキミも同じだろ…アイラ？", "2025/08/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "シナツ", 129201, -285.0, 0.0, 14.465);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10208, 1020803, 301, "アイラの世界のみんなも、\n一生懸命生きてるんだ…！\n…なのに、世界の危機だなんて！\nあたし、絶対、絶対力になるよ！", "2025/08/31 12:00:00", 4, 3, "2030/01/01 14:59:59", "ヒヨリ", 100101, -285.0, 0.0, 8.662);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10208, 1020804, 401, "シナツ…もう一人の私\n彼女が大切にしている世界を\n絶対に終わらせはしない…！\n彼女と笑い合える未来のために！", "2025/08/31 12:00:00", 1, 3, "2030/01/01 14:59:59", "レイ", 100301, -285.0, 0.0, 10.915);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10209, 1020901, 101, "あ、ど、ども…\nあ、あんま見ねぇでくれっと\nその、恥ずかしくて…\n体が熱くなって、溶けちまう…", "2025/09/30 12:00:00", 1, 7, "2030/01/01 14:59:59", "ユキノ", 135802, -285.0, 0.0, 6.042);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10209, 1020902, 201, "なんで俺が幽霊に…意味不明だ\nこれじゃ死んでも死にきれねェ\n悪ィがこれも何かの縁だ、\nよろしく頼むぜ騎士公", "2025/09/30 12:00:00", 4, 1, "2030/01/01 14:59:59", "サツキ", 135903, -285.0, 0.0, 9.402);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10209, 1020903, 301, "う、うじゅる…\nサツキ姉しゃまを助けるためにも\nまずは、腹ごしらえでしゅね…！\nタコ焼き、召し上がってくだしゃい", "2025/09/30 12:00:00", 8, 1, "2030/01/01 14:59:59", "ルルィ", 136902, -285.0, 0.0, 8.16);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10210, 1021001, 101, "ひさしぶりのジオ・ゲヘナ\n歓迎パーティーで起きた殺人事件！\nランドソルの名探偵の名にかけて\n解き明かしてみせようじゃないか！", "2025/10/31 12:00:00", 3, 2, "2030/01/01 14:59:59", "カスミ", 137401, -285.0, 0.0, 5.0);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10210, 1021002, 201, "…わたしはどこの誰なのでしょう\nどこから来てどこへ行くのでしょう\nこの事件の真相が分かれば、\n何かが掴めるような気がいたします", "2025/10/31 12:00:00", 4, 1, "2030/01/01 14:59:59", "アラクネ", 136001, -285.0, 0.0, 8.12);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10210, 1021003, 301, "ナーナ、すいりとかろんぱとか、\nむずかしくてわかんないけど…\nお友だちとドーナツのためならっ\nせーいっぱいがんばるよっ！", "2025/10/31 12:00:00", 4, 2, "2030/01/01 14:59:59", "ナーナ", 136202, -285.0, 0.0, 6.386);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10211, 1021101, 101, "騎士クンに想いを伝えたい…だから\nミソラちゃん、わたしがんばるね！\n…あれ？　わたし、なんでこんなに\n必死になってるんだろう…？", "2025/11/30 12:00:00", 3, 4, "2030/01/01 14:59:59", "ユイ", 136402, -285.0, 0.0, 7.438);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10211, 1021102, 201, "騎士さんとのデートはわたしに\nお任せです★　なのでユイさんは\n安心して計画に専念して…たくさん\nドキドキしちゃってくださいね", "2025/11/30 12:00:00", 1, 5, "2030/01/01 14:59:59", "ミソラ", 136302, -285.0, 0.0, 8.35);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10212, 1021201, 101, "新しい年がやってくるね！\nお正月の料理も飾りつけも、\nお姉ちゃんにお任せだよ♪\nみんなで楽しく新年を迎えよう！", "2025/12/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "シズル", 136501, -285.0, 0.0, 5.0);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10212, 1021202, 201, "おうちの掃除もトラブルも、 \n世界を揺るがす大ピンチも！\n今年のことは今年のうちに \nサッサと片付けちゃいますっ！", "2025/12/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "リノ", 137902, -285.0, 0.0, 5.25);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10212, 1021203, 301, "年の瀬はギルドマスターも\nつい駆け回っちゃう忙しい時期だ！\n一年いろいろあったけど\nお互いイイ年を迎えよう、少年！", "2025/12/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "ラビリスタ", 106801, -285.0, 0.0, 7.5);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10213, 1021301, 101, "漂う異国の料理の香り…それだけで\n世界中を旅した気分に浸れますね\nふふ、ほらクローチェ\nあまりはしゃぎすぎないように…♪", "2026/01/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "ライラエル", 126501, -285.0, 0.0, 8.204);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10213, 1021302, 201, "異界の姫君と煮炊きにて競い合う…\n感奮興起、いとをかし\n華やかなる祭典と相成るよう、一肌\n脱がせていただきとう存じまする", "2026/01/31 12:00:00", 1, 2, "2030/01/01 14:59:59", "ネフィ＝ネラ", 129701, -285.0, 0.0, 7.176);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10213, 1021303, 301, "『ビストロプリンセス』のために\n料理の特訓を重ねてまいりました\nこの料理で騎士様の…い、いえ\n皆様の心とお腹を満たしますわ！", "2026/01/31 12:00:00", 1, 5, "2030/01/01 14:59:59", "ヴァイオレット", 133101, -285.0, 0.0, 8.602);
INSERT INTO `v1_a0dde6d08116f9322806a5bb986b23b3d14f0c581e0da3872c6d7166d052b5b3` VALUES (61, 10213, 1021304, 102, "多様な味わいが織りなすハーモニー\nそれこそが我が国の矜持です\nもしもそれを乱すようであれば\nわたしも優しくはありませんよ？", "2026/02/06 12:00:00", 1, 3, "2030/01/01 14:59:59", "ライラエル", 138101, -285.0, 0.0, 10.14);
