INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 100, "やばいですね☆なの！", 1, 1, 5, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "ペコリーヌのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 101, "主さま、なの！", 2, 1, 5, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "コッコロのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 102, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 3, 0, 5, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "キャルのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 103, "おにーたん♪なの！", 4, 1, 5, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "シェフィのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 104, "ハーハッハッハ！\n世界征服なのじゃなのー！", 5, 1, 5, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "イリヤのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 105, "お兄ちゃんのエッチ！　なの！", 6, 1, 5, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "アカリのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 106, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 7, 1, 5, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "ヨリのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 107, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 8, 0, 5, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "シノブのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 108, "インフェルノシールド！なの！", 9, 1, 5, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "ジュンのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 109, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 10, 1, 5, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "トモのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 110, "タイガーヒーローボンバー！\nなの！", 11, 1, 5, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "マツリのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 111, "ま、いいんじゃん？\nしらんけどなの", 13, 1, 5, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "クロエのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 112, "ぷりーん★なの！", 14, 1, 5, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "チエルのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 113, "端的に換言すれば\n青春の味なの？", 15, 1, 5, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "ユニのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 114, "幼なじみはあんた…なの？", 16, 1, 5, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "サレンのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 115, "ひゃああんなの～！", 17, 1, 5, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "スズメのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 116, "サルキデバレヤ！なの！", 18, 0, 5, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "クルミのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 117, "プリンフルスイングなの～！", 19, 1, 5, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "アヤネのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 118, "みらくるみやりん\nくるりんぱ～☆なの！", 20, 1, 5, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "マホのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 119, "ウルフェンバイトなの！", 21, 1, 5, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "マコトのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 120, "なんくるないさ～！なの！", 22, 1, 5, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "カオリのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 121, "ふふん。簡単な推理なの", 23, 1, 5, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "カスミのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 122, "エリザベス～！なの～！", 24, 1, 5, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "マヒルのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 123, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 25, 1, 5, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "シオリのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 124, "もふもふにしてあげるなの！", 26, 1, 5, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "リマのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 125, "めんどくさいな～なの…", 27, 0, 5, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "リンのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 126, "プリンの権能を解放するの～！", 28, 1, 5, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "ラビリスタのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 127, "お姉ちゃんパワー全開！なの！", 29, 0, 5, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "シズルのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 128, "お兄ちゃん、プリンをよこすの！", 30, 1, 5, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "リノのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 129, "困った時はお互いさまさま！\nなの！", 31, 1, 5, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "ヒヨリのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 130, "フラワリングヒールなの～♪", 32, 1, 5, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "ユイのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 131, "スラッシュテンペスト！なの。", 33, 1, 5, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "レイのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 132, "おーほっほほほ！なの！", 34, 1, 5, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "アキノのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 133, "プリしゅわ最高～！なの！", 35, 1, 5, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "ユカリのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 134, "にゃにゃにゃ！なの！", 36, 1, 5, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "タマキのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 135, "効率全開！なの！", 37, 0, 5, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "ミフユのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 136, "あらあら～なの～♪", 38, 1, 5, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "ミサトのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 137, "スヤスヤ～…なの…", 39, 1, 5, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "ハツネのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 138, "ぼっちは卒業なの～！", 40, 1, 5, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "アオイのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 139, "リズムあげるのー！", 41, 1, 5, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "ノゾミのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 140, "風の加護を！なの！", 42, 1, 5, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "チカのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 141, "騎士さんのために\nしたんじゃありません！なの！", 43, 1, 5, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "ツムギのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 142, "宴の始まりなの！", 12, 0, 5, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "クリスティーナのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 143, "一刀両断、覚悟しな！なの！", 44, 1, 5, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "ルカのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 144, "きゅぴーん！キタコレ！なの！", 45, 1, 5, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "ナナカのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 145, "人呼んで、疾風のミヤコなの！", 46, 1, 5, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "アンナのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 146, "クスクス…クスクスクス…なの", 47, 1, 5, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "エリコのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 147, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 48, 1, 5, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "ミツキのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 148, "私は子どもではない！なの！", 49, 1, 5, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "モニカのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 149, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 50, 1, 5, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ニノンのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 150, "…ぐふふ、じゅるり～なの", 51, 0, 5, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "クウカのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 151, "あぁ、ミヤコはなんて美しいの～", 52, 1, 5, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "ユキのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 152, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 53, 0, 5, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "アユミのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 153, "にひひ…イタズラの時間なの！", 54, 0, 5, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "ミソギのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 154, "ぴょんぴょこぴょーん、なの♪", 55, 0, 5, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ミミのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 155, "へんたいふしんしゃさんなの！", 56, 0, 5, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "キョウカのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 156, "ハート狙い撃ち！なの！", 57, 1, 5, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "スズナのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 157, "授業を始めます♪なの！", 58, 0, 5, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "イオのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 158, "立派なレディ、なの！", 59, 1, 5, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "ミサキのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 159, "プリンを持ってこないと\nお仕置きだよ～☆なの！", 60, 1, 5, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "ホマレのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 160, "たぎってきたの！", 61, 1, 5, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "カヤのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 161, "下剋上、やってやるですなの！", 62, 0, 5, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "イノリのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 162, "どけどけ～！なの～！", 63, 1, 5, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "ムイミのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 163, "ミラーミラー！なの！", 64, 1, 5, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ネネカのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 164, "プリンに忠誠を誓いますわ！\nなの！", 65, 1, 5, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "クレジッタのプリン");
INSERT INTO `v1_a1a2e3f81a63680656c3a1c270ff99604f10e36bfc2122b971521a27f4ffa0f1` VALUES (3, 165, "ギルド管理協会へようこそ！\nなの！", 66, 1, 5, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "カリンのプリン");
