INSERT INTO `v1_a299dbb870bb146ccd5bdc54aed56a43daf577833c79503a68531d4d1bf254c9` VALUES (5, 1, 8.0, 0, 24005, "2019/01/31 12:00:00", 6.0, "エリコ（バレンタイン）", 2, "今回は調子が悪かった……のですか？\nあなた様が手伝ってくれたものなら\nわたしはどんなものでも……クスクス", 24, "2030/12/17 14:59:59", 10011, -385.0, 109001);
INSERT INTO `v1_a299dbb870bb146ccd5bdc54aed56a43daf577833c79503a68531d4d1bf254c9` VALUES (6, 1, 0.0, 0, 24006, "2019/01/31 12:00:00", 0.0, "エリコ（バレンタイン）", 1, "あなた様が手がけた\nチョコレートケーキは\n私にとっての宝物……\n侮辱する者は生かしておきませんわ", 24, "2030/12/17 14:59:59", 10011, 0.0, 109002);
INSERT INTO `v1_a299dbb870bb146ccd5bdc54aed56a43daf577833c79503a68531d4d1bf254c9` VALUES (7, 1, 0.0, 0, 24007, "2019/01/31 12:00:00", 0.0, "エリコ（バレンタイン）", 1, "とても素晴らしい出来栄えですわ！\nあなた様にふさわしい\nチョコレートケーキができました", 24, "2030/12/17 14:59:59", 10011, 0.0, 109002);
INSERT INTO `v1_a299dbb870bb146ccd5bdc54aed56a43daf577833c79503a68531d4d1bf254c9` VALUES (8, 1, 0.0, 0, 24008, "2019/01/31 12:00:00", 0.0, "エリコ（バレンタイン）", 1, "私を手伝ってくれたあなた様は\nすでに私の虜……\n甘い世界に誘って差し上げますわ", 24, "2030/12/17 14:59:59", 10011, 0.0, 109002);
INSERT INTO `v1_a299dbb870bb146ccd5bdc54aed56a43daf577833c79503a68531d4d1bf254c9` VALUES (9, 1, 0.0, 0, 24009, "2019/01/31 12:00:00", 0.0, "エリコ（バレンタイン）", 1, "あなた様の愛情\nしかと受け止めました\nこれが……私たちの初めての\n共同作業ですわね……クスクス", 24, "2030/12/17 14:59:59", 10011, 0.0, 109002);
INSERT INTO `v1_a299dbb870bb146ccd5bdc54aed56a43daf577833c79503a68531d4d1bf254c9` VALUES (10, 1, 0.0, 0, 24010, "2019/01/31 12:00:00", 0.0, "エリコ（バレンタイン）", 1, "これは世界一の\nチョコレートケーキですわ\nだって、あなた様が腕によりをかけて\n作ってくださったんですもの", 24, "2030/12/17 14:59:59", 10011, 0.0, 109002);
INSERT INTO `v1_a299dbb870bb146ccd5bdc54aed56a43daf577833c79503a68531d4d1bf254c9` VALUES (1, 1, 8.0, 0, 24011, "2019/01/31 12:00:00", 2.12, "シズル（バレンタイン）", 2, "上手にできなくて残念だった？\nでも、弟くんの気持ちが嬉しいな\nいつだってお姉ちゃんは\nそばにいるからね♪", 24, "2030/12/17 14:59:59", 10011, -385.0, 109101);
INSERT INTO `v1_a299dbb870bb146ccd5bdc54aed56a43daf577833c79503a68531d4d1bf254c9` VALUES (2, 5, 8.0, 0, 24012, "2019/01/31 12:00:00", 4.19, "シズル（バレンタイン）", 2, "うーん、弟くんには珍しく\n緊張しちゃったのかな？\nもう一度チャレンジしてみる？", 24, "2030/12/17 14:59:59", 10011, -385.0, 109101);
