INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (2, 1, 10118, "フリーマーケットを訪れたジータが出会ったのは\n城をこっそり抜け出していたペコリーヌだった。", 0, 0, "フリーマーケット会場", 40, 2834, 5118061, "ペコリーヌとの出会い", "青空の下で謁見", 8, 91002, 7);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (2, 1, 10118, "ジータたちがフリーマーケットを巡っていると\n露店を出していたマホに声をかけられる。", 0, 0, "フリーマーケット会場", 40, 2835, 5118062, "マホとの出会い", "未来の騎空団\nマスコット？", 8, 91002, 7);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (2, 1, 10118, "ジータが騎空団への依頼を確認していると、\n高笑いをしながらアキノが現れる。", 0, 0, "ランドソル市街地", 40, 2837, 5118063, "アキノとの出会い", "お嬢さまからの\n緊急依頼", 8, 91002, 7);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (2, 1, 10118, "ジータたちは、フリーマーケット会場で\n少し困った様子のジュンを見かける。", 0, 0, "フリーマーケット会場", 40, 4108402, 5118064, "ジュンとの出会い", "黄金色の悩みごと", 8, 91002, 18);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (2, 1, 10118, "フリーマーケットの隅で開かれる格闘大会──\nそこにはカオリもいて……？", 0, 0, "フリーマーケット会場", 40, 4107402, 5118065, "カオリとの出会い", "白熱！　格闘大会！", 8, 91002, 18);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (2, 1, 10118, "太鼓祭を控えるオーエド町──\nジータは、困った様子のニノンと出会う。", 0, 1, "オーエド町", 40, 9000227, 5118066, "ニノンとの出会い", "祭り太鼓はオーエドの華", 8, 91002, 16);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (3, 1, 10118, "ビカラはイベントに出演中のスズナを見かける。\nしかし、スズナはどこか困っているようで……？", 0, 0, "フリーマーケット会場", 40, 2101181, 5118067, "スズナとの出会い", "人気者は遅れて\nやってくる", 8, 91002, 19);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (3, 1, 10118, "ビカラたちが街を散策していると、通りかかった\nナナカが興奮した様子で近づいてきて……？", 0, 0, "ランドソル市街地", 40, 4109402, 5118068, "ナナカとの出会い", "巫女・ミーツ・魔法少女", 8, 91002, 18);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (3, 1, 10118, "ネズ耳をなくしてしまい絶望するビカラ。同じ頃\nキャルとコッコロは落とし物を拾っており……", 0, 0, "フリーマーケット会場", 40, 4305402, 5118069, "キャル＆コッコロとの出会い", "ネズ耳は二度なくなる", 8, 91002, 18);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (3, 1, 10118, "街外れでひと休みをしていたビカラたちは、\nすっかりゴキゲンな様子のユカリと出会う。", 0, 0, "ランドソル市街地", 40, 4101402, 5118070, "ユカリとの出会い", "この出会いにカンパイ！", 8, 91002, 18);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (3, 1, 10118, "ビカラは、人混みに怯えるアオイを心配し、\n陽気に声をかけるも……", 0, 0, "ランドソル市街地", 40, 4106402, 5118071, "アオイとの出会い", "陰陽アンサンブル", 8, 91002, 18);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (3, 1, 10118, "ビカラがフリーマーケット会場を歩いていると\n占いをしていたシノブに声をかけられる。", 0, 1, "フリーマーケット会場", 40, 2836, 5118072, "シノブとの出会い", "運試し十番勝負！", 8, 91002, 7);
INSERT INTO `v1_a3d9f5e5f2b98bedd59c7580c635959090837454daaf3addb5e5290913cf8318` VALUES (1, 0, 10118, 0, 10145115, 0, 0, 0, 0, 5118600, "オープニング", "彼女たちの新たな日常", 0, 0, 0);
