INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1002001, 12, "2020-02-15 15:00:00", 190801, 1002, "2030-04-01 14:00:00", 0, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！");
INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1003001, 11, "2020-02-15 15:00:00", 190801, 1003, "2030-04-01 14:00:00", 0, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1004001, 3, "2020-02-15 15:00:00", 190801, 1004, "2030-04-01 14:00:00", 0, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！");
INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1005001, 7, "2020-02-15 15:00:00", 190801, 1005, "2030-04-01 14:00:00", 0, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。");
INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1006001, 2, "2020-05-31 12:00:00", 190801, 1006, "2030-04-01 14:00:00", 0, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！");
INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1007001, 4, "2020-08-15 15:00:00", 190801, 1007, "2030-04-01 14:00:00", 0, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう");
