INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1010001, 4, "2022-02-15 15:00:00", 190801, 1010, "2030-04-01 14:00:00", 0, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪");
INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1011001, 6, "2022-07-18 15:00:00", 190801, 1011, "2030-04-01 14:00:00", 0, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪");
INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1012001, 6, "2023-09-18 15:00:00", 190801, 1012, "2030-04-01 14:00:00", 0, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪");
INSERT INTO `v1_a3fb308b5e799773ad7df0d4e0c61a41f5bb01dc08b59341ee7432a3077a156e` VALUES (1013001, 6, "2024-04-18 15:00:00", 190801, 1013, "2030-04-01 14:00:00", 0, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪");
