INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ペコリーヌのプリン", 3, 1, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 5, 1, 100, "やばいですね☆なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("コッコロのプリン", 3, 2, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 5, 1, 101, "主さま、なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("キャルのプリン", 3, 3, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 5, 0, 102, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("シェフィのプリン", 3, 4, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 5, 1, 103, "おにーたん♪なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("イリヤのプリン", 3, 5, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 5, 1, 104, "ハーハッハッハ！\n世界征服なのじゃなのー！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("アカリのプリン", 3, 6, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 5, 1, 105, "お兄ちゃんのエッチ！　なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ヨリのプリン", 3, 7, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 5, 1, 106, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("シノブのプリン", 3, 8, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 5, 0, 107, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ジュンのプリン", 3, 9, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 5, 1, 108, "インフェルノシールド！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("トモのプリン", 3, 10, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 5, 1, 109, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("マツリのプリン", 3, 11, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 5, 1, 110, "タイガーヒーローボンバー！\nなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("クロエのプリン", 3, 13, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 5, 1, 111, "ま、いいんじゃん？\nしらんけどなの");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("チエルのプリン", 3, 14, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 5, 1, 112, "ぷりーん★なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ユニのプリン", 3, 15, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 5, 1, 113, "端的に換言すれば\n青春の味なの？");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("サレンのプリン", 3, 16, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 5, 1, 114, "幼なじみはあんた…なの？");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("スズメのプリン", 3, 17, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 5, 1, 115, "ひゃああんなの～！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("クルミのプリン", 3, 18, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 5, 0, 116, "サルキデバレヤ！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("アヤネのプリン", 3, 19, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 5, 1, 117, "プリンフルスイングなの～！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("マホのプリン", 3, 20, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 5, 1, 118, "みらくるみやりん\nくるりんぱ～☆なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("マコトのプリン", 3, 21, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 5, 1, 119, "ウルフェンバイトなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("カオリのプリン", 3, 22, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 5, 1, 120, "なんくるないさ～！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("カスミのプリン", 3, 23, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 5, 1, 121, "ふふん。簡単な推理なの");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("マヒルのプリン", 3, 24, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 5, 1, 122, "エリザベス～！なの～！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("シオリのプリン", 3, 25, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 5, 1, 123, "私の好きな本…読んで\nもらえたらうれしいです…\nなの");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("リマのプリン", 3, 26, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 5, 1, 124, "もふもふにしてあげるなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("リンのプリン", 3, 27, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 5, 0, 125, "めんどくさいな～なの…");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ラビリスタのプリン", 3, 28, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 5, 1, 126, "プリンの権能を解放するの～！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("シズルのプリン", 3, 29, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 5, 0, 127, "お姉ちゃんパワー全開！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("リノのプリン", 3, 30, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 5, 1, 128, "お兄ちゃん、プリンをよこすの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ヒヨリのプリン", 3, 31, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 5, 1, 129, "困った時はお互いさまさま！\nなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ユイのプリン", 3, 32, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 5, 1, 130, "フラワリングヒールなの～♪");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("レイのプリン", 3, 33, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 5, 1, 131, "スラッシュテンペスト！なの。");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("アキノのプリン", 3, 34, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 5, 1, 132, "おーほっほほほ！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ユカリのプリン", 3, 35, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 5, 1, 133, "プリしゅわ最高～！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("タマキのプリン", 3, 36, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 5, 1, 134, "にゃにゃにゃ！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ミフユのプリン", 3, 37, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 5, 0, 135, "効率全開！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ミサトのプリン", 3, 38, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 5, 1, 136, "あらあら～なの～♪");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ハツネのプリン", 3, 39, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 5, 1, 137, "スヤスヤ～…なの…");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("アオイのプリン", 3, 40, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 5, 1, 138, "ぼっちは卒業なの～！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ノゾミのプリン", 3, 41, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 5, 1, 139, "リズムあげるのー！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("チカのプリン", 3, 42, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 5, 1, 140, "風の加護を！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ツムギのプリン", 3, 43, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 5, 1, 141, "騎士さんのために\nしたんじゃありません！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("クリスティーナのプリン", 3, 12, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 5, 0, 142, "宴の始まりなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ルカのプリン", 3, 44, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 5, 1, 143, "一刀両断、覚悟しな！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ナナカのプリン", 3, 45, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 5, 1, 144, "きゅぴーん！キタコレ！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("アンナのプリン", 3, 46, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 5, 1, 145, "人呼んで、疾風のミヤコなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("エリコのプリン", 3, 47, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 5, 1, 146, "クスクス…クスクスクス…なの");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ミツキのプリン", 3, 48, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 5, 1, 147, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("モニカのプリン", 3, 49, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 5, 1, 148, "私は子どもではない！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ニノンのプリン", 3, 50, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 5, 1, 149, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("クウカのプリン", 3, 51, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 5, 0, 150, "…ぐふふ、じゅるり～なの");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ユキのプリン", 3, 52, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 5, 1, 151, "あぁ、ミヤコはなんて美しいの～");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("アユミのプリン", 3, 53, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 5, 0, 152, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ミソギのプリン", 3, 54, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 5, 0, 153, "にひひ…イタズラの時間なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ミミのプリン", 3, 55, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 5, 0, 154, "ぴょんぴょこぴょーん、なの♪");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("キョウカのプリン", 3, 56, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 5, 0, 155, "へんたいふしんしゃさんなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("スズナのプリン", 3, 57, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 5, 1, 156, "ハート狙い撃ち！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("イオのプリン", 3, 58, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 5, 0, 157, "授業を始めます♪なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ミサキのプリン", 3, 59, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 5, 1, 158, "立派なレディ、なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ホマレのプリン", 3, 60, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 5, 1, 159, "プリンを持ってこないと\nお仕置きだよ～☆なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("カヤのプリン", 3, 61, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 5, 1, 160, "たぎってきたの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("イノリのプリン", 3, 62, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 5, 0, 161, "下剋上、やってやるですなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ムイミのプリン", 3, 63, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 5, 1, 162, "どけどけ～！なの～！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("ネネカのプリン", 3, 64, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 5, 1, 163, "ミラーミラー！なの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("クレジッタのプリン", 3, 65, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 5, 1, 164, "プリンに忠誠を誓いますわ！\nなの！");
INSERT INTO `v1_a61504ab457c08c5ea95977bc8c61cd43e56e771801bfa9df83f4625c4eaf337` VALUES ("カリンのプリン", 3, 66, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 5, 1, 165, "ギルド管理協会へようこそ！\nなの！");
