INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 1, 3, 0, 3, 0, 2669, 105601, 1);
