INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 1, 2, 0, 1, 0, 2683, 101301, 1);
