INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 1, 2, 0, 4, 0, 2709, 190901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1, 2, 0, 1, 1, 2710, 190901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 1, 2, 0, 2, 2, 2711, 190901, 1);
