INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 1, 3, 0, 7, 0, 2719, 190901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 1, 3, 0, 8, 0, 2720, 190901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("メリークリスマス！\n歌で幸せを\n届けます…！", 1, 1, 0, 1, 0, 2722, 108401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 1, 1, 0, 2, 0, 2723, 108401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 1, 1, 0, 3, 0, 2724, 108401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("曲が書けるのは\nあなたが側に\nいてくれるからです…", 1, 1, 0, 4, 0, 2725, 108401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 1, 1, 0, 5, 0, 2726, 108401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("クリスマスの幸せを\nあなたと\n感じ合えたらな…", 4, 1, 0, 101, 0, 2727, 108401, 1);
