INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お暇でしたら\nクリスマスのお祝いを\nしませんか？", 1, 2, 0, 3, 0, 2731, 108401, 1);
