INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 4, 2, 0, 101, 0, 2733, 108401, 1);
