INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1, 2, 0, 1, 1, 3141, 109701, 1);
