INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 1, 0, 1, 0, 3153, 109801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1, 1, 0, 2, 0, 3154, 109801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1, 1, 0, 3, 0, 3155, 109801, 1);
