INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1, 1, 0, 1, 0, 4531, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 1, 1, 0, 2, 0, 4532, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 1, 1, 0, 3, 0, 4533, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 1, 1, 0, 4, 0, 4534, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 1, 1, 0, 5, 0, 4535, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 4, 1, 0, 101, 0, 4536, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 8, 1, 0, 102, 0, 4537, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("もし時間があるなら、\n私と街の見回りに\n行かない？", 1, 2, 0, 1, 0, 4538, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 1, 2, 0, 2, 0, 4539, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 1, 2, 0, 3, 0, 4540, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 1, 2, 0, 4, 0, 4541, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 4, 2, 0, 101, 0, 4542, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 8, 2, 0, 102, 0, 4543, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1, 2, 0, 1, 1, 4544, 114301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 1, 2, 0, 2, 2, 4545, 114301, 1);
