INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1, 1, 0, 1, 0, 4556, 114401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 1, 1, 0, 2, 0, 4557, 114401, 1);
