INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 1, 2, 0, 1, 0, 4563, 114401, 1);
