INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 1, 1, 0, 4, 0, 4769, 115601, 1);
