INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 1, 2, 0, 3, 0, 4775, 115601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 1, 2, 0, 4, 0, 4776, 115601, 1);
