INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 1, 1, 0, 5, 0, 7979, 129001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 4, 1, 0, 101, 0, 7980, 129001, 1);
