INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お招きありがとう\nそれで…何をして\nいればいい…かな？", 1, 2, 0, 1, 0, 7982, 129001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 1, 2, 0, 2, 0, 7983, 129001, 1);
