INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 1, 2, 0, 2, 2, 7989, 129001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 1, 2, 0, 3, 3, 7990, 129001, 1);
