INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 1, 2, 0, 2, 0, 8008, 129301, 1);
