INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 1, 2, 0, 3, 3, 8015, 129301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 1, 3, 0, 2, 0, 8017, 129301, 1);
