INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 1, 3, 0, 5, 0, 8020, 129301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ええい、\n固形食物はいらん！　\nポーションを出せ！", 1, 3, 0, 7, 0, 8022, 129301, 1);
