INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 1, 3, 0, 10, 0, 8025, 129301, 1);
