INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1, 1, 0, 2, 0, 8034, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1, 1, 0, 3, 0, 8035, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1, 1, 0, 4, 0, 8036, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1, 1, 0, 5, 0, 8037, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 4, 1, 0, 101, 0, 8038, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 8, 1, 0, 102, 0, 8039, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 1, 2, 0, 1, 0, 8040, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 1, 2, 0, 2, 0, 8041, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 1, 2, 0, 3, 0, 8042, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 1, 2, 0, 4, 0, 8043, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 4, 2, 0, 101, 0, 8044, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 8, 2, 0, 102, 0, 8045, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 2, 0, 1, 1, 8046, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 1, 2, 0, 2, 2, 8047, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 1, 2, 0, 3, 3, 8048, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 1, 3, 0, 2, 0, 8050, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 1, 3, 0, 3, 0, 8051, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 1, 3, 0, 4, 0, 8052, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 1, 3, 0, 5, 0, 8053, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 1, 3, 0, 7, 0, 8055, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 1, 3, 0, 8, 0, 8056, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 1, 3, 0, 10, 0, 8058, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 1, 3, 0, 11, 0, 8059, 129401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("さてと…\n明日の訓練メニュー\n考えねえとな", 1, 1, 0, 1, 0, 8060, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1, 1, 0, 2, 0, 8061, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1, 1, 0, 3, 0, 8062, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1, 1, 0, 4, 0, 8063, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1, 1, 0, 5, 0, 8064, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 4, 1, 0, 101, 0, 8065, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 8, 1, 0, 102, 0, 8066, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 1, 2, 0, 1, 0, 8067, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 1, 2, 0, 2, 0, 8068, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 1, 2, 0, 3, 0, 8069, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 1, 2, 0, 4, 0, 8070, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 4, 2, 0, 101, 0, 8071, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 8, 2, 0, 102, 0, 8072, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 2, 0, 1, 1, 8073, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 1, 2, 0, 2, 2, 8074, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 1, 2, 0, 3, 3, 8075, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 1, 3, 0, 2, 0, 8077, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 1, 3, 0, 3, 0, 8078, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 1, 3, 0, 4, 0, 8079, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 1, 3, 0, 5, 0, 8080, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 1, 3, 0, 7, 0, 8082, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 1, 3, 0, 8, 0, 8083, 129801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 1, 0, 1, 0, 8085, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1, 1, 0, 2, 0, 8086, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1, 1, 0, 3, 0, 8087, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1, 1, 0, 4, 0, 8088, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1, 1, 0, 5, 0, 8089, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 4, 1, 0, 101, 0, 8090, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 8, 1, 0, 102, 0, 8091, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 1, 2, 0, 1, 0, 8092, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 1, 2, 0, 2, 0, 8093, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 1, 2, 0, 3, 0, 8094, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 1, 2, 0, 4, 0, 8095, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 4, 2, 0, 101, 0, 8096, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 8, 2, 0, 102, 0, 8097, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 2, 0, 1, 1, 8098, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 1, 2, 0, 2, 2, 8099, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 1, 2, 0, 3, 3, 8100, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 1, 3, 0, 2, 0, 8102, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 1, 3, 0, 3, 0, 8103, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 1, 3, 0, 4, 0, 8104, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 1, 3, 0, 5, 0, 8105, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 1, 3, 0, 7, 0, 8107, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 1, 3, 0, 8, 0, 8108, 129901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("……（そわそわ）", 1, 1, 0, 1, 0, 8110, 129601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ここに寄らずの花陣…\n作ったらダメですよね", 1, 1, 0, 2, 0, 8111, 129601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1, 1, 0, 3, 0, 8112, 129601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("身を隠せる場所が\nあると…\n助かるんですけど", 1, 1, 0, 4, 0, 8113, 129601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1, 1, 0, 5, 0, 8114, 129601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("出入りするひと\nみんな、笑顔…\n…変わった家ですね", 4, 1, 0, 101, 0, 8115, 129601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この家なら安全─\nかもしれません\n魔物が出ないので", 8, 1, 0, 102, 0, 8116, 129601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("これも、視察の一環\n遊びに来たわけじゃ\nないです", 1, 2, 0, 1, 0, 8117, 129601, 1);
