INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1, 2, 0, 1, 1, 8460, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 1, 2, 0, 2, 2, 8461, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 1, 2, 0, 3, 3, 8462, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 1, 3, 0, 2, 0, 8464, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("細かいところまで\nすごーく丁寧に\n作られてる…", 1, 3, 0, 3, 0, 8465, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 1, 3, 0, 4, 0, 8466, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 1, 3, 0, 5, 0, 8467, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 1, 3, 0, 7, 0, 8469, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 1, 3, 0, 8, 0, 8470, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 1, 3, 0, 10, 0, 8472, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 1, 3, 0, 11, 0, 8473, 131901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 1, 0, 1, 0, 8474, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1, 1, 0, 2, 0, 8475, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1, 1, 0, 3, 0, 8476, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1, 1, 0, 4, 0, 8477, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1, 1, 0, 5, 0, 8478, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 4, 1, 0, 101, 0, 8479, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 8, 1, 0, 102, 0, 8480, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 1, 2, 0, 1, 0, 8481, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 1, 2, 0, 2, 0, 8482, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 1, 2, 0, 3, 0, 8483, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 1, 2, 0, 4, 0, 8484, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 4, 2, 0, 101, 0, 8485, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 8, 2, 0, 102, 0, 8486, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 2, 0, 1, 1, 8487, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 1, 2, 0, 2, 2, 8488, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 1, 2, 0, 3, 3, 8489, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 1, 3, 0, 2, 0, 8491, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 1, 3, 0, 3, 0, 8492, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 1, 3, 0, 4, 0, 8493, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 1, 3, 0, 5, 0, 8494, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 1, 3, 0, 7, 0, 8496, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("海に関する詩文なども\nあるのでしょうか…\n気になります", 1, 3, 0, 8, 0, 8497, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 1, 3, 0, 10, 0, 8499, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 1, 3, 0, 11, 0, 8500, 132101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("これがこの世界の\n居住環境か…\n面白い構造だね", 1, 1, 0, 1, 0, 8501, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 1, 1, 0, 2, 0, 8502, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("目新しいものが\nたくさんあるね\n胸が躍るよ", 1, 1, 0, 3, 0, 8503, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 1, 1, 0, 4, 0, 8504, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 1, 1, 0, 5, 0, 8505, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 4, 1, 0, 101, 0, 8506, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("普段と違う格好で\n歩き回るのも\n悪くない経験だ", 8, 1, 0, 102, 0, 8507, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 1, 2, 0, 1, 0, 8508, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("気軽に魔女に\n話しかけるなんて\n君は物好きだね", 1, 2, 0, 2, 0, 8509, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この世界においては\nボクは異物だからね\n大人しくしているさ", 1, 2, 0, 3, 0, 8510, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 1, 2, 0, 4, 0, 8511, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 4, 2, 0, 101, 0, 8512, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 8, 2, 0, 102, 0, 8513, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1, 2, 0, 1, 1, 8514, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 1, 2, 0, 2, 2, 8515, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 1, 2, 0, 3, 3, 8516, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 1, 3, 0, 2, 0, 8518, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 1, 3, 0, 3, 0, 8519, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("いい香りだ\nこれにはどんな\n効能があるんだろう？", 1, 3, 0, 4, 0, 8520, 132001, 1);
