INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 1, 3, 0, 7, 0, 8523, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("素晴らしいね\nここには叡智が\n詰まっている", 1, 3, 0, 8, 0, 8524, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 1, 3, 0, 10, 0, 8526, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 1, 3, 0, 11, 0, 8527, 132001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 12, 1, 0, 103, 0, 8528, 109701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 12, 2, 0, 103, 0, 8529, 109701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 12, 1, 0, 103, 0, 8530, 109801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 12, 2, 0, 103, 0, 8531, 109801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 12, 1, 0, 103, 0, 8532, 109901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 12, 2, 0, 103, 0, 8533, 109901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1, 1, 0, 1, 0, 8534, 132201, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 1, 1, 0, 2, 0, 8535, 132201, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 1, 1, 0, 3, 0, 8536, 132201, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 1, 1, 0, 4, 0, 8537, 132201, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 1, 1, 0, 5, 0, 8538, 132201, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 4, 1, 0, 101, 0, 8539, 132201, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 8, 1, 0, 102, 0, 8540, 132201, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 1, 2, 0, 1, 0, 8541, 132201, 1);
