INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 4, 1, 0, 101, 0, 8770, 133001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 8, 1, 0, 102, 0, 8771, 133001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 1, 2, 0, 1, 0, 8772, 133001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 1, 2, 0, 2, 0, 8773, 133001, 1);
