INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 1, 2, 0, 4, 0, 8775, 133001, 1);
