INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 1, 2, 0, 1, 1, 8778, 133001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 1, 2, 0, 2, 2, 8779, 133001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 1, 2, 0, 3, 3, 8780, 133001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 1, 3, 0, 2, 0, 8782, 133001, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 1, 3, 0, 3, 0, 8783, 133001, 1);
