INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("わたしの水着姿…\n少しは大人っぽいって\n思ってくれたかな…？", 4, 1, 0, 101, 0, 9303, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("家の中は涼しいのに\nお兄さんのこと\n考えると暑くなって…\n夏バテしちゃいそう…", 8, 1, 0, 102, 0, 9304, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お兄さんと一緒なら\n夏休みの日記\n書くことが多すぎて\nページが足りません♪", 1, 2, 0, 1, 0, 9305, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("かき氷食べましょ♪\n…ふふ、お兄さん\n舌の色すごいですよ♪\nわたしも…べ～っ♪", 1, 2, 0, 2, 0, 9306, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("今日は海に行きます？\nそれともお家で\nのんびりですか？\nわたしはどっちでも♪", 1, 2, 0, 3, 0, 9307, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夏バテしないように\nスタミナ料理のレシピ\nお勉強しました♪", 1, 2, 0, 4, 0, 9308, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夏が終わっちゃっても\nこの水着を着てたら\n夏の思い出、忘れずに\nいてくれますか…？", 4, 2, 0, 101, 0, 9309, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…この日焼け跡\nお兄さんと\nわたしだけの\n秘密…ですよ？", 8, 2, 0, 102, 0, 9310, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おはようございます！\n朝の体操１、２、３♪\nしゃきっと目覚めて\nいい一日にしましょ♪", 1, 2, 0, 1, 1, 9311, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お昼休憩ですね♪\n午後からも頑張れる様\nお兄さんのこと\n癒しますね…♪", 1, 2, 0, 2, 2, 9312, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("そろそろ寝る時間…\nお兄さんが眠るまで\n一緒にいますね…\n…ふあぁ…", 1, 2, 0, 3, 3, 9313, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("気持ちいいけど、汗…\n染みちゃわないように\n気をつけないと…", 1, 3, 0, 2, 0, 9315, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ぎゅう～♪\nなんて、ちょっと\nあざといかな…♪", 1, 3, 0, 3, 0, 9316, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夏にぴったりの\nフルーティーな香り♪\nお兄さん、好きかな？", 1, 3, 0, 4, 0, 9317, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("水着で暖まるのって\nなんだか贅沢な気分♪\nこたつでアイス的な♪", 1, 3, 0, 5, 0, 9318, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お腹ぽっこりに\nならないよう\n気をつけないと…\nでも止まらない…", 1, 3, 0, 7, 0, 9320, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("弾ける水着でビーチの\n視線を独り占め…かぁ\nわたしはお兄さんの\n視線だけで…なんて♪", 1, 3, 0, 8, 0, 9321, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("これで夏の大冒険に\nしゅっぱ～つ！\n…なんちゃって♪", 1, 3, 0, 10, 0, 9323, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("なんだか女神さま\nみたい…？\nふふ、わたしには\n似合わないかもです♪", 1, 3, 0, 11, 0, 9324, 135301, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("真っ白い日差し─\nジオ・ゲヘナには\nなかったものですね\n─悪くはないです", 1, 1, 0, 1, 0, 9325, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("しばらくここで\n休んでます\n人の多い街中よりは\n落ち着くので─", 1, 1, 0, 2, 0, 9326, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ここにも─\nあるんでしょうか\n海で使う遊び道具…", 1, 1, 0, 3, 0, 9327, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("見張りは─\nここならあまり\n必要なさそう…", 1, 1, 0, 4, 0, 9328, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夏のバカンス─\n媛が楽しんでるなら\n私は十分です", 1, 1, 0, 5, 0, 9329, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("知らない相手は、\n苦手だけど─\n…少しだけ、\n話してみても…", 4, 1, 0, 101, 0, 9330, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ここにいると\n安心する、ような…\n土壌が─良い…？", 8, 1, 0, 102, 0, 9331, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夏の正装、水着─\n着方、間違えて\nないですよね…？", 1, 2, 0, 1, 0, 9332, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("相変わらず、\nおしゃべりが\n好きなんですね\n私は─普通です", 1, 2, 0, 2, 0, 9333, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("─喉が渇きました\n…いえ、花にお水が\n欲しいわけじゃなくて", 1, 2, 0, 3, 0, 9334, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("─いつもより\n花がつやつやしてる？\n…あ、褒めて\nくれたんですか", 1, 2, 0, 4, 0, 9335, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この部屋─\n貴方が飾りつけを？\nわかります、何となく", 4, 2, 0, 101, 0, 9336, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("すぐ傍にいると、\n暑くなりませんか？\n─私は別に\nかまいませんが", 8, 2, 0, 102, 0, 9337, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("朝から元気ですね─\nおかげですっかり\n目が覚めました", 1, 2, 0, 1, 1, 9338, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("外─暑そうです\n出かけるなら\n気を付けてください\n倒れたりしないよう…", 1, 2, 0, 2, 2, 9339, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("─貴方も\n夜更かしですか？\n…少しだけなら\n付き合いますよ", 1, 2, 0, 3, 3, 9340, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("貴方もここで\n休みたいですか？\n─ダメです\n今は私が使うので", 1, 3, 0, 2, 0, 9342, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ゆるい顔つきですね\nジャングルでは\n生き残れそうに\nないです─", 1, 3, 0, 3, 0, 9343, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("─ん…\nこれ、海の香り？\n─いい匂い…", 1, 3, 0, 4, 0, 9344, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…暑さには\n慣れてますよ\n寒いのよりは、ずっと", 1, 3, 0, 5, 0, 9345, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("栄養は、大事です\n味は─あまり\n気にしたこと、\nありませんでした", 1, 3, 0, 7, 0, 9347, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("適当に本を読んでいる\nだけです─\n…別に、遊びについて\n調べたりしてません", 1, 3, 0, 8, 0, 9348, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("魔物を模した\n人形─ですね\n戦闘訓練用に？", 1, 3, 0, 10, 0, 9350, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("貝のクッションですか\n─噛みついてきたり\nしないですよね", 1, 3, 0, 11, 0, 9351, 135401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("シントーメッキャク\n火もまた涼し…\nむむむ…やっぱり夏は\n暑いデース！", 1, 1, 0, 1, 0, 9352, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("海にも新しい忍法の\nヒントがあるはずデス\nショーグンと海底散歩\nも楽しそうデス", 1, 1, 0, 2, 0, 9353, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("釣りの心得は\nメイキョーシスイ…\n忍者の修行に\nうってつけデス♪", 1, 1, 0, 3, 0, 9354, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("天下統一の後のことも\n考えねばなりまセン\n今度はテンジョー統一\nデスかね…？", 1, 1, 0, 4, 0, 9355, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("デケデケデン♪\nテルテル坊主完成デス\n晴れてないとお外で\n遊べまセンからね！", 1, 1, 0, 5, 0, 9356, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("次のオモテナシも\nショーグンをワクワク\nさせマス♪　今から\n用意しておかねばデス", 4, 1, 0, 101, 0, 9357, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("新しい忍法の開発に\n休みはないのデス！\nショーグンの天下統一\nを思えばなんのその！", 8, 1, 0, 102, 0, 9358, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("海の前に準備運動デス\nまずは手を後ろに、\n素早く忍者走り！\nシュバババッ", 1, 2, 0, 1, 0, 9359, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夏にお祭りは\n欠かせないデスよね♪\n地域のイベント情報は\n見逃しまセン！", 1, 2, 0, 2, 0, 9360, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ショーグン、団扇で\nあおいであげマス！\n忍法・扇風の術ー！\nうりゃりゃりゃー！", 1, 2, 0, 3, 0, 9361, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ワタシが必要な時は\n風鈴を鳴らすデス！\n風よりも早く\n駆けつけマスよ！", 1, 2, 0, 4, 0, 9362, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("じっと見つめられると\n顔が熱くなってきて…\nはっ、ショーグンの\n新しい忍法デスか！？", 4, 2, 0, 101, 0, 9363, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ショーグン、日焼けは\nノン、デスよ！\nワタシがクリームぬり\nぬりしてあげマース！", 8, 2, 0, 102, 0, 9364, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おはようデス！\n朝ごはんはワタシに\nお任せデス。\n毎日みそ汁作るデス！", 1, 2, 0, 1, 1, 9365, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("日差しに注意デスよ\nネッチューショーに\n日焼け、どっちも\n大変デスからね！", 1, 2, 0, 2, 2, 9366, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ショーグン、\n外で花火しまショウ！\nぱちぱちキレイで\n楽しいデスよ！", 1, 2, 0, 3, 3, 9367, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("遊びに備えて\n今は休む時…\nガシンショータンデス", 1, 3, 0, 2, 0, 9369, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この子たちにも\n手伝ってもらって\n新しい忍法を開発\nしてみまショウ♪", 1, 3, 0, 3, 0, 9370, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("むむっ、ここまで\n潮の香りが漂って\nきた気がしマス！", 1, 3, 0, 4, 0, 9371, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("海に入った後は\n体を温めねば\nやはりサウナも\n必要デスね！", 1, 3, 0, 5, 0, 9372, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("いっぱい食べて\nいっぱい遊ぶデス！\n夏バテなんかに\n負けてられまセン！", 1, 3, 0, 7, 0, 9374, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("『BUSHIDO』の\n最新刊が待ち遠しい\nデス…マイゾーキン\n特集、読みたいデス", 1, 3, 0, 8, 0, 9375, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ショーグンとこの子に\n乗って、天下統一まで\nシュババババッ！\nなんてどうでショウ", 1, 3, 0, 10, 0, 9377, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ぽよんぽよ～ん♪\nショーグンと一緒に\n遊んでみたいデス♪", 1, 3, 0, 11, 0, 9378, 137101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("今度はシナツちゃんの\n世界へ行ってみたいな\nエリスちゃんも誘って\n大人女子旅とか最高♪", 1, 1, 0, 1, 0, 9379, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あっはっはー！\nよし、今日も絶好調！\nま、いつも笑ってたら\n不調なんてないけど♪", 1, 1, 0, 2, 0, 9380, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ん～あっちの世界と\nほとんど一緒だね！\n違ってるところ探しに\n出かけてみよっかな？", 1, 1, 0, 3, 0, 9381, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ううーん、のんびり\nするのもいいけど\nどこか行ってみたいな\n人助けとか、ね", 1, 1, 0, 4, 0, 9382, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あっ、そーだ\n向こうへ帰る前に\nこっちのお土産とか\n買っとかなくちゃ", 1, 1, 0, 5, 0, 9383, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふ～ん、騎士くんって\nこういうのが好みか～\n後でヒヨリちゃんに\n教えてあげよ♪", 4, 1, 0, 101, 0, 9384, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("次はあたしが恩を返す\n番だね。騎士くんに\n助けが必要になったら\n絶対駆けつけるよ！", 8, 1, 0, 102, 0, 9385, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あっ騎士くん！\n困ってることない？\nお姉さんがなんでも\n力になっちゃうよ！", 1, 2, 0, 1, 0, 9386, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("また闘技場に行こうよ\nちょっと運動不足でさ\nバーンと暴れたい気分\nなんだよね♪", 1, 2, 0, 2, 0, 9387, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("いつか大人になったら\n一緒に飲もうよ！\nあははっ、騎士くん\n指切りげんまん♪", 1, 2, 0, 3, 0, 9388, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("一緒に作った麦しゅわ\nカップ、最高だね♪\n騎士くんの分もどうぞ\nちゃんとジュースだよ", 1, 2, 0, 4, 0, 9389, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ねえ…ぶっちゃけ\nヒヨリちゃんのこと\nどう思ってるのかな？\nお姉さんに聞かせなよ", 4, 2, 0, 101, 0, 9390, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士くん、うっかり\n飲みすぎたあの日の\nコトは誰にも内緒で\nお願いね。約束だよ！", 8, 2, 0, 102, 0, 9391, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あはは、騎士くん\n新しい朝だよー！\n今日はどんな一日か\nワクワクするね～♪", 1, 2, 0, 1, 1, 9392, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("午後からも笑顔で\n元気に乗り切ろうね！\n外に行くもよし、\n部屋で麦しゅわもよし", 1, 2, 0, 2, 2, 9393, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ここからは大人の時間\n騎士くんにはまだ少し\n早いかな～？\nだから、おやすみ♪", 1, 2, 0, 3, 3, 9394, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("麦しゅわ飲み過ぎた\n夜はここにダーイブ\n…なんて大人に\nなっちゃダメだよ～？", 1, 3, 0, 2, 0, 9396, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あたしの世界の子たち\nこういうの好きかな？\nお土産に探してみよう\n気に入るかも♪", 1, 3, 0, 3, 0, 9397, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ん～いいね♪\nあとでお気に入りの\nフレーバーも\n試してみよっかな？", 1, 3, 0, 4, 0, 9398, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ほうほう…熱い！\nサラサリアは\nいつも暖かいから\nこういうの新鮮！", 1, 3, 0, 5, 0, 9399, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ん～おいしい！\nけどやっぱご飯は\nみんなで一緒に\n食べたいな～", 1, 3, 0, 7, 0, 9401, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("文字ばっかの本は\nちょーっと苦手かも…\n必要なら読むけどさ？", 1, 3, 0, 8, 0, 9402, 135501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("皆思い思いに過ごして\nいるのに、不思議と\n落ち着く。いつまでも\nここにいられたら…", 1, 1, 0, 1, 0, 9406, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("皆に挨拶をせねば\n…いや、今は、まだ\nタイミングが悪い…\nまた今度にしよう…", 1, 1, 0, 2, 0, 9407, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("私の服装…この場で\n浮いてないだろうか\nまずい、心配に\nなってきた…！", 1, 1, 0, 3, 0, 9408, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("アイラたちとの旅行も\n楽しかったな…\nまた３人で温泉に\nいきたいものだ", 1, 1, 0, 4, 0, 9409, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ここで剣の稽古は\nさすがによろしくない\n代わりに今日は瞑想を\nすることにしよう", 1, 1, 0, 5, 0, 9410, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("人とのつながりは\n大切にしなければ\n今日こそは誰かに\n話しかけるぞ…！", 4, 1, 0, 101, 0, 9411, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("できることが増えて、\n迷うばかりの毎日…\nそれが楽しく尊いと\n今実感している…", 8, 1, 0, 102, 0, 9412, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ええと…奇遇だな\nこんなところで\n会うなんて。いや違う\n…その、こんにちは…", 1, 2, 0, 1, 0, 9413, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("弟子たちへの土産も\n探しておかないとな\nよければキミからも\nアドバイスを頼む…", 1, 2, 0, 3, 0, 9415, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("剣の稽古をしたい？\nふっ…いいとも\nキミとならいくらでも\n準備をしてすぐ戻る", 1, 2, 0, 4, 0, 9416, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("よければまた、\n街を案内してくれ\n名所旧跡もだがキミと\n歩くのは楽しいんだ", 4, 2, 0, 101, 0, 9417, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("感謝の心を\n忘れたことはないよ\nキミたちの優しさに\n救われた一人として…", 8, 2, 0, 102, 0, 9418, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("うむ、いい顔だな\n良い一日は、\n良い目覚めから始まる\n心掛けておくといい", 1, 2, 0, 1, 1, 9419, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("もう昼時か\n誰かを誘って食事に…\n…いや無理だ、\nハードルが高い…！", 1, 2, 0, 2, 2, 9420, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夜はいい…\n誰とも話さず\n一人きりでいても、\n不自然ではないからな", 1, 2, 0, 3, 3, 9421, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…いかんな、これは\n気を抜くとこのまま\n離れられなくなる…", 1, 3, 0, 2, 0, 9423, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("シナツだ、よろしく\n……ふぅ\n人形相手の練習なら\n緊張しないのだがな…", 1, 3, 0, 3, 0, 9424, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふむ…集中力が\n高まりそうな香りだな\n稽古にも役立つだろう\n皆にも教えてやろうか", 1, 3, 0, 4, 0, 9425, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("凍える人に寄り添い\n温もりを与える…\n私もそんな存在に\nなりたいものだ", 1, 3, 0, 5, 0, 9426, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("食の楽しみ…\nあまり意識して\nいなかったが、\nやはりいいものだな", 1, 3, 0, 7, 0, 9428, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…っと、いかん\n読みふけっていたら\nいくらでも時間を\n過ごせてしまうぞ…", 1, 3, 0, 8, 0, 9429, 135601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("また空を飛びたいな～\nふわふわ～ってなる\nあの感じが気持ちいい\nんだよね～♪", 1, 1, 0, 1, 0, 9433, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("またサラサリアに\n行きたいなー\n…串焼きの味が\n忘れられなくって♪", 1, 1, 0, 2, 0, 9434, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("悩んでる人がいたら\n背中を押してあげたい\nあたしがそれで、勇気\nづけられたみたいに", 1, 1, 0, 3, 0, 9435, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("気持ちを言葉にするの\n大事って分かってても\nいざとなると緊張して\n上手くできないなぁ…", 1, 1, 0, 4, 0, 9436, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あっちの世界でも\nギルドハウスは\nあるのかな…？\n今度探してみよっと♪", 1, 1, 0, 5, 0, 9437, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("アイラに会って少し\n成長できたかも…！\n素敵な女の人目指して\nもっと頑張るぞーっ♪", 4, 1, 0, 101, 0, 9438, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あっちのあたしは\n騎士くんに選ばれた…\nよし、あたしも\n頑張らなくっちゃ…！", 8, 1, 0, 102, 0, 9439, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("部屋の模様替えする？\nサラサリア風の家具も\nあったら素敵だよね！", 1, 2, 0, 1, 0, 9440, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("なんだかお疲れ気味？\nそうだっ、サラサリア\nで教わったマッサージ\nをしてあげるっ♪", 1, 2, 0, 2, 0, 9441, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士くんっ！\n今からあたしとデート\nしませんかっ！", 1, 2, 0, 3, 0, 9442, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("今ちょうど、踊りの\n練習してたとこなの\n騎士くんも\n一緒にどう？", 1, 2, 0, 4, 0, 9443, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("き、騎士くん！\nこの前の続き…\n手、繋いでいい…？", 4, 2, 0, 101, 0, 9444, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("えへへへ、騎士くんが\nあたしの背中を押して\nくれると、勇気も\n100万倍だよっ♪", 8, 2, 0, 102, 0, 9445, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おはよ、騎士くん！\n今日もいい朝だよ！\nサラサリアみたいに\n太陽が輝いてるもん！", 1, 2, 0, 1, 1, 9446, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("フレフレ、騎士くん！\n午後もファイトー！\nあたしは日陰でお昼寝\nタイム…なんてねっ♪", 1, 2, 0, 2, 2, 9447, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夜に騎士くんといると\n空飛ぶ絨毯から見た\nサラサリアの夜景を\n思い出しちゃうな♪", 1, 2, 0, 3, 3, 9448, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("休憩きゅうけ～い♪\n強い人と戦った後は\nしっかりと体を\n休ませないとね", 1, 3, 0, 2, 0, 9450, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("にゃ～、\nかわいいにゃ～♪\nアンブラを\n思い出しちゃうよ♪", 1, 3, 0, 3, 0, 9451, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("わっ…すごくいい\n匂い～サラサリアの\n砂漠の匂いにちょっと\n似てるかも", 1, 3, 0, 4, 0, 9452, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("サラサリアの太陽\nみたいに暖かい♪\n日向ぼっこしてる\nみたいでしあわせ…", 1, 3, 0, 5, 0, 9453, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ん～おいしいっ！\nアイラもきっと\n好きな味だね…！\n今度食べてもらおっ！", 1, 3, 0, 7, 0, 9455, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("これは恋愛指南本…！\nふむ…意中の男性と\nキスするには…って\nこれはまだ早いよ～！", 1, 3, 0, 8, 0, 9456, 135701, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あてがここにいたら、\nみんな寒い思いを\nしちまうかな…\nご、ごめんな", 1, 1, 0, 1, 0, 9460, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あて、みんなより\n体でっけぇし…\nもうちょっと隅っこ\n行っとこうかな", 1, 1, 0, 2, 0, 9461, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("さ、騒がしいとこは\n苦手だども…\nここはそんなに\n気になんね…", 1, 1, 0, 3, 0, 9462, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("いろいろ勉強してぇな\nイナカもんだから、\nみんなに迷惑とか\nかけねぇように…", 1, 1, 0, 4, 0, 9463, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("家具とか、飾りとか、\n壊しちまったら大変…\nき、気ぃつけねぇとっ", 1, 1, 0, 5, 0, 9464, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あ、あての三味線、\n聴きたいの…？\nこ、心の準備が\nできてからで、い…？", 4, 1, 0, 101, 0, 9465, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("っ、ぁぶね…\nあてったらまた、\n騎士さのこと見て、\n溶けそうになってた…", 8, 1, 0, 102, 0, 9466, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お、おめさ、その…\nおやつさん、いらね？\nかき氷とかなら、\nあて、作れるよ…？", 1, 2, 0, 1, 0, 9467, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あ、あてに…何か用？\n…声、聴きたかった？\nな、なら…えっと…\nおしゃべり…する？", 1, 2, 0, 2, 0, 9468, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("こっちさ来てから…\n向こうの世界の\n何年分も、おしゃべり\nしたかもしんね…", 1, 2, 0, 3, 0, 9469, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("こっちの世界には、\n氷滑りって遊びが\nあるって…？\nあてにもできっかな…", 1, 2, 0, 4, 0, 9470, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ん…あては元気だよ\n悩みがあっても、\nおめさの声もらうと\n吹っ飛んじまうし…", 4, 2, 0, 101, 0, 9471, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士さが近くに\nいてくれる…\n今は、それだけで\nいいんだ…うん…", 8, 2, 0, 102, 0, 9472, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お、おはよ…\nおめさのおかげで、\n昨夜もよく眠れたよ…", 1, 2, 0, 1, 1, 9473, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…あっ、もう昼飯の\n時間かぁ…\n考えごとしてたら\nあっという間だよ", 1, 2, 0, 2, 2, 9474, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("景色は似てるけど…\nこの世界の夜は、\nざわざわ音がして\n賑やかだな…", 1, 2, 0, 3, 3, 9475, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あんま縮こまってると\n体硬くなっちまうし…\nたまにはのんびり…", 1, 3, 0, 2, 0, 9477, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("えへへ、めんこいな…\nそだ、仁太坊たちも\n並んでたら\nもっとめんこいかも…", 1, 3, 0, 3, 0, 9478, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("この匂い…なんか、\nは、はいから…？\nあて、イナカもん\nだから…", 1, 3, 0, 4, 0, 9479, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あ、暑くて溶けそう…\nだども、我慢できたら\n修行になる…かも…？", 1, 3, 0, 5, 0, 9480, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ん…♪　うめぇ…\nこんなうめぇもん\n毎日食べられるの、\n幸せだなぁ…", 1, 3, 0, 7, 0, 9482, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("こっちの世界にも…\n雪女が出る物語、\nあるみてぇだな…\nか、悲しい話だども…", 1, 3, 0, 8, 0, 9483, 135801, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("なかなか上等な\n家じゃねェか\nちっとばかし\n軒を借りるぜ", 1, 1, 0, 1, 0, 9487, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("俺の棒っきれの\n置き場所は…\nま、玄関にでも\n立てかけときゃいいか", 1, 1, 0, 2, 0, 9488, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("こっちの世界は\n眩しくって敵わねェぜ\n目がチカチカしやがる", 1, 1, 0, 3, 0, 9489, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("建物の様式が同じでも\n世界と住人が違えば\n印象も変わるモンだな", 1, 1, 0, 4, 0, 9490, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("街の連中は\n戦いなんて知らねェ\nのんきなツラしてんな\n…いいトコじゃねェか", 1, 1, 0, 5, 0, 9491, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士公のヤツ、\nまたぞろ厄介事に\n巻き込まれや\nしてねェだろうな…", 4, 1, 0, 101, 0, 9492, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふわぁ…\nこうも居心地がいいと\n眠くなっちまうぜ", 8, 1, 0, 102, 0, 9493, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("俺ばっかり\n見てんじゃねェよ\n赤鬼になっちまわぁ", 1, 2, 0, 1, 0, 9494, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("注いでくれんのか？\nあんがとよ\nんじゃ俺からも\nお返しだぜ", 1, 2, 0, 2, 0, 9495, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おう、暇してるなら\n鍛錬に付き合えよ\nじっとしてっと\n体がなまって仕方ねェ", 1, 2, 0, 3, 0, 9496, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("退屈してきたな…\n散歩にでも行くか？\n騎士公なら退屈しねェ\n場所知ってるだろ", 1, 2, 0, 4, 0, 9497, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("女との関係は\nきちっとしとけよ\n俺だって助けらんねぇ\nもんはあるからな", 4, 2, 0, 101, 0, 9498, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("角に触りたいだぁ？\n…まあ、騎士公の\n頼みなら…\nあんま手荒にすんなよ", 8, 2, 0, 102, 0, 9499, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("起きろ\n朝の鍛錬の時間だぜ\nほれ、さっさと\n身支度しちまいな", 1, 2, 0, 1, 1, 9500, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("昼飯ィ？\n俺の分はいらねぇよ\n気ィつかわねぇで\nくんな", 1, 2, 0, 2, 2, 9501, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("まだ起きてんのか？\nさっさと寝ねェと\nこうやって鬼が来るぜ", 1, 2, 0, 3, 3, 9502, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("悪くない座り心地だな\n気ィ抜くと、ダメに\nされちまいそうだぜ", 1, 3, 0, 2, 0, 9504, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…今こいつ動いたか？\nグレ公あたりが\n悪さしてんじゃ\nねェだろうな", 1, 3, 0, 3, 0, 9505, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士公はこういう\n香りが好みか？\nお姫とユキノに\n教えてやるとすっか", 1, 3, 0, 4, 0, 9506, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("地獄の釜よりかは\nちとぬるいが…\nなかなか\nいい火力してやがるぜ", 1, 3, 0, 5, 0, 9507, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("こっちの世界の本にも\n鬼が出てくるのか", 1, 3, 0, 8, 0, 9510, 135901, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("フランシュシュ１号、\nサクラです！　サガの\n心を、歌でみんなに\n届けにきました！", 1, 1, 0, 1, 0, 9514, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ランドソルはドキドキ\nいっぱいの街やけん！\nサガとどっちが\n暮らしやすかろうねっ", 1, 1, 0, 2, 0, 9515, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ソロ活動がんばって、\n憧れの子にもも～っと\n近づくと！　成長した\n私、早く見せたか～！", 1, 1, 0, 3, 0, 9516, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("部屋におるとは、伝説\nのメンバーばかり…！\n私もこのランドソルで\n伝説ば刻むとよ！", 1, 1, 0, 4, 0, 9517, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あそこの修繕が気に\nなると…。…あのっ！\nあ、ついあの子を\n呼びそうに…えへへっ", 1, 1, 0, 5, 0, 9518, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("アイドルのレッスン、\n気になると？　まず、\n脚をぐい～っと広げて\n…あわわ、戻らん！？", 4, 1, 0, 101, 0, 9519, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士さんのコネが\nあれば、いつか堂々と\nゾンビアイドルで\n売り出せるかもっ…！", 8, 1, 0, 102, 0, 9520, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おはようござい\nまーす！　挨拶は基本\nやけん、元気よくっ！\nさ、一緒にやろう！", 1, 2, 0, 1, 0, 9521, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("身だしなみチェック、\nよしっ。今日もいい\n天気っ♪　さあ、\nいってきまーーす！！", 1, 2, 0, 2, 0, 9522, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あんまり引っ張ると、\nう、腕が～…！　あ、\n気にせんで。伸びても\nすぐ元に戻るけんっ♪", 1, 2, 0, 3, 0, 9523, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("私、持っとらんって\n思ってきたけど、それ\nが何ね！　友達ば、\nいっぱい持っとるけん", 1, 2, 0, 4, 0, 9524, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("嚙めば噛むほど味が\n出る…騎士さんは\nサガのアタリメ\nみたいな人やねっ♪", 4, 2, 0, 101, 0, 9525, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("騎士さんにだけ教え\nますね。私の秘密…\n私の死因を。え？\nもう分かる気もする？", 8, 2, 0, 102, 0, 9526, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ぼ～っとしとかれんね\n「ぶっ殺すぞ」って\n気合入れてくれる子が\n両方の街におるけんっ", 1, 2, 0, 1, 1, 9527, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あのすごか食べっぷり\nよう思い出すとよ\n元気かな…変なもん\n食べてないかな…", 1, 2, 0, 2, 2, 9528, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夜、出歩くと、怖～い\nアンデッドに襲われる\nかもしれんよ！\n気をつけんと…！", 1, 2, 0, 3, 3, 9529, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("リラックス！　もっと\nがんばってリラックス\nせんといかんね！\nむむむ～っ！", 1, 3, 0, 2, 0, 9531, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("が～ばや～らしか～♪\nあの子のお土産に\nしたいけど、同じの\n売っとらんかな…？", 1, 3, 0, 3, 0, 9532, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ん～、いい香りっ♪\nいつもこやん素敵な\n香りのする人が、私の\n友達におってね…", 1, 3, 0, 4, 0, 9533, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("え～と…熱いとその…\n傷んじゃいそうで、\nちょっと心配やけん…\nそ…その…私の体が", 1, 3, 0, 5, 0, 9534, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("わあ、美味しか～っ！\nランドソルのグルメも\nなかなか手強いっ…！\nサガも負けられんね～", 1, 3, 0, 7, 0, 9536, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("アイドル雑誌はあると\nですかね？　フラン\nシュシュのランキング\nは…しょぼぼぼーん…", 1, 3, 0, 8, 0, 9537, 136101, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("名探偵から聖職者へ、\n華麗なる変装だ！\nいや、心の中まで\n敬虔に…変貌、かな♪", 1, 1, 0, 1, 0, 9541, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("真実の鏡で、誰の謎も\n解いてみせるよ。\n恋の謎は…私が解いて\nほしいくらいだ…", 1, 1, 0, 2, 0, 9542, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("歩き方も…厳かに…\n気品と高潔さが\n感じられるように\n練習しなくてはね", 1, 1, 0, 3, 0, 9543, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("私の行いはきっと、\nどこかで見られている\nかつてお別れをした\n大切なあの子にも…ね", 1, 1, 0, 4, 0, 9544, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("異議あり！　…う～ん\nもっと威厳をもって…\n異議あり！　はっ、\n聞いていたのかいっ！", 1, 1, 0, 5, 0, 9545, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("静かに心を見つめれば\n大切なものが分かる\n助手くんとの時間の\nかけがえなさも、ね", 4, 1, 0, 101, 0, 9546, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("私も、想い合う二人の\n神聖な誓約の儀と\nいうものに憧れるよ\nつまり結婚式、にね…", 8, 1, 0, 102, 0, 9547, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("珈琲を一杯お願い\nするよ、助手くん\nこれがないと私の\n推理が冴えなくてね", 1, 2, 0, 1, 0, 9548, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("懺悔があるなら聞き\n届けよう。…え～っ！\n棚にある私のチョコを\n食べてしまったって！", 1, 2, 0, 2, 0, 9549, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("助手くん、今日の\n依頼は何件かな\nもし誰も来ないなら\n一緒にお出かけしよう", 1, 2, 0, 3, 0, 9550, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("さあ、出かけようか\n君と私で巡礼の旅だ\n鞄の中身はただ一つ\n真実を愛する心、さ！", 1, 2, 0, 4, 0, 9551, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("一緒に祈ってくれる\nかい？　ありがとう\n一人では真実が重荷に\nなるときもあってね", 4, 2, 0, 101, 0, 9552, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("何でも話してほしい\n分かち合いたいんだ\n喜びも悲しみも、君が\n背負う、なにもかも", 8, 2, 0, 102, 0, 9553, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おはよう！　聖職者は\n早起きなのさ♪\nふわぁ…はっ、今の\nあくびは見てないね？", 1, 2, 0, 1, 1, 9554, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("今日のお昼は…\n一緒に台所で\n作らないかい？\n新婚さんみたいに♪", 1, 2, 0, 2, 2, 9555, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("夜のお祈りをしよう\n凶悪な事件なんて、\nもう起きないように", 1, 2, 0, 3, 3, 9556, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("はあ…ふかふか～…\n堕落してしまいそう…\nなんとも罪なアイテム\nだねえ、これは…", 1, 3, 0, 2, 0, 9558, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("かわいいな…♪\n探偵事務所の机に\n置いたら場を和ませて\nくれそうだね", 1, 3, 0, 3, 0, 9559, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("はあ…心が落ち着く\nけして、その、小説の\n謎解きにイライラして\nいたわけではないよ？", 1, 3, 0, 4, 0, 9560, 137401, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…暖かいね。こんな\n暖かさをあまねく\n世に届けるのが、\n今の私の役目だよ", 1, 3, 0, 5, 0, 9561, 137401, 1);
