INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("主さまのために\nできることも\n増えました…\nふふ、胸が躍ります", 4, 1, 0, 101, 0, 9658, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("わたくしの願いは、\n主さまと共にあること\nいわば、生涯を\n添い遂げるように…", 8, 1, 0, 102, 0, 9659, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("新たな姿でも変わらず\nおはようから\nおやすみまで\nお供いたしますよ", 1, 2, 0, 1, 0, 9660, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("装いも、大人の体に\n相応しいものに…\nエレガントさを\n心がけてみました", 1, 2, 0, 2, 0, 9661, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("昨日の買い出しでは\n少々買いすぎましたが\n大荷物でも平気でした\nすばらしいです…♪", 1, 2, 0, 3, 0, 9662, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("主さまのための\n大事な体ですからね\n長い髪の手入れも\n念入りにいたします", 1, 2, 0, 4, 0, 9663, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("本日の添い寝は\n大人と子ども、\nどちらの姿に\nいたしましょうか", 4, 2, 0, 101, 0, 9664, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("あなたさま…\nあぁいえ、その…\n少し違う呼び名を\n試してみようかと", 8, 2, 0, 102, 0, 9665, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おはようございます\n主さまとのご挨拶で\n始まる一日…\nワクワクいたします", 1, 2, 0, 1, 1, 9666, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おや、もうお昼時…\nお腹の空き具合が\n違うと、少々感覚が\n鈍ってしまいますね", 1, 2, 0, 2, 2, 9667, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("おやすみの際には\nわたくしの子守唄を…\n安眠の力も以前より\n増しておりますよ♪", 1, 2, 0, 3, 3, 9668, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("いつでも主さまに\n尽くせるよう、\n体を休めることも\n大事です…ふぅ", 1, 3, 0, 2, 0, 9670, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("愛らしいものを\n慈しむ心は、\n大人になっても\n変わりません…", 1, 3, 0, 3, 0, 9671, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("大人の体になると、\n匂いの感じかたも\n少し変わるような…？\n不思議なものです", 1, 3, 0, 4, 0, 9672, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("手足が冷えないよう\nしっかり温めて…\n冷え性は大人の悩みと\n伺いましたので", 1, 3, 0, 5, 0, 9673, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("胃のほうも、前より\n大きくなった模様です\n手が止まりません…\nもぐもぐ…", 1, 3, 0, 7, 0, 9675, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("久々に、主さまに\n本の読み聞かせを\nしましょうか\nふふ、楽しみです", 1, 3, 0, 8, 0, 9676, 137601, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("弟くんは何が好きかな\n縁起物がいっぱいの\nおせち料理を\n食べてほしいんだよね", 1, 1, 0, 1, 0, 9682, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ちょっと…帯がきつい\nかも…おせち料理の\n食べすぎかな？\n気を付けなくちゃ", 1, 1, 0, 2, 0, 9683, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("初日の出は見なくちゃ\n新しい一年の始まりを\n告げるお日さまに\n挨拶しておきたいし", 1, 1, 0, 3, 0, 9684, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("甘酒って不思議…\nすっごく寒い日に\n飲むと、もっと\nおいしくなるんだもん", 1, 1, 0, 4, 0, 9685, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("着物のお姉ちゃん♪\nいつもとは違う格好で\n弟くんをドキドキ\nさせちゃおっと♪", 1, 1, 0, 5, 0, 9686, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("うふふ…弟くんの寝顔\nかわいかったなぁ♪\nもはや国宝級だよ！", 4, 1, 0, 101, 0, 9687, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("弟くんの着物姿、\nかっこよかった～♪\nはぁぁ…思い出したら\nドキドキしてきた…♪", 8, 1, 0, 102, 0, 9688, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("私の着物が気になる？\n触ってもいいよ\n肌触りがよくって、\n着心地も最高なんだ～", 1, 2, 0, 1, 0, 9689, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("初詣に行くときは手を\n繋ごうね。人混みの\n中ではぐれたりしたら\n大変だもんっ", 1, 2, 0, 2, 0, 9690, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("叶えたいことが\nあったら言ってね？\nお姉ちゃんが全力で\n協力するから！", 1, 2, 0, 3, 0, 9691, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お正月は一緒に\n過ごしてお買い物も\nして…ラブラブな\n時間を楽しもうねっ♪", 1, 2, 0, 4, 0, 9692, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("…大好きだよ、弟くん\nふふっ、急にごめんね\nどうしても今、伝えた\nいって思っちゃったの", 4, 2, 0, 101, 0, 9693, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("弟くんにもらった指輪\n宝物だよ♪　だけど\nいつか本当の新婚さん\nになりたいな…♪", 8, 2, 0, 102, 0, 9694, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お正月の朝は空気が\n冷たいよね。こんな\nときは、太陽の光を\n浴びながらお散歩しよ", 1, 2, 0, 1, 1, 9695, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お昼はお雑煮！\n出汁も具材も\nこだわったから、\n楽しみにしててね♪", 1, 2, 0, 2, 2, 9696, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("そろそろ寝よっか？\nそうだ、添い寝をして\nあげる！　寒いとなか\nなか寝付けないでしょ", 1, 2, 0, 3, 3, 9697, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("座り方には気を付け\nないと。きれいな\n着物に、しわは作り\nたくないもんね", 1, 3, 0, 2, 0, 9699, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ふわふわでかわいいね\nこの子たちも、\n弟くんとの生活、\nお祝いしてくれてる？", 1, 3, 0, 3, 0, 9700, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("くんくん…神社のお香\nみたい♪　いいこと\nありそうな気が\nしてきたよ", 1, 3, 0, 4, 0, 9701, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("寒い日に暖炉はありが\nたいな～♪　ここで\nお餅を焼けば、お腹も\n膨れて一石二鳥かも♪", 1, 3, 0, 5, 0, 9702, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("お腹いっぱい…でも\nまだ食べたい！　う～\nこれじゃ、正月太りへ\nまっしぐらだよ～", 1, 3, 0, 7, 0, 9704, 136501, 1);
INSERT INTO `v1_a839e308f820e4af09441a02da168b7a11003e90984c152f57a7c63a525dd2e9` VALUES ("ラブラブだけじゃなく\nもくもくと一緒に本を\n読むのもいいよね♪\nお正月は時間もあるし", 1, 3, 0, 8, 0, 9705, 136501, 1);
