INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 7, 0, 4.5, 206100, 2, 270, 206100, "ワイズワンド", 0, 0, 1080, "【魔法】遠距離攻撃\n浴びれば凍える冷気の魔法で、\n戦意を奪うオウムガイ型の魔物。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 1, 0, 3.5, 206200, 1, 270, 206200, "ギャングオッター", 0, 0, 650, "【物理】中距離攻撃\n貝殻をぶつけて不意をつく、\n意地悪な北海のチンピラっこ。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 1, 0, 0.5, 206201, 1, 270, 206201, "ノーティビーバー", 0, 0, 650, "【物理】中距離攻撃\n片っ端から木を削り倒すビーバー。\n削った木片は家作りだけでなく、\n家を守るための飛び道具となる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 0, 0, 10.0, 206300, 0, 270, 206300, "ルナポッド", 0, 0, 1110, "【回復】単体ＴＰ回復\nルナの塔建造と共に造られた、エネルギー増幅器。\n漂う魔力を濃縮し、供給することで守護者を強化する。");
