INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 0, 0, 2.5, 206401, 1, 270, 206400, "リトルアイ", 0, 0, 100, "【物理】近接攻撃\n一ツ目族の王に仕える、小さな\n召使い。王様がくれた金ピカ\n装備が、大のお気に入り。");
