INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 1, 0, 2.295, 206700, 1, 270, 206700, "ヒヨリのシャドウ", 0, 0, 200, "ヒヨリの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理大ダメージを与え、\n　その周りの狭い範囲の敵すべてにも\n　物理中ダメージを与える。\n・目の前の敵１キャラに物理中ダメージを\n　与える。\n・目の前の敵１キャラに物理小ダメージを与え、\n　さらに自分の物理攻撃力を中アップさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 4, 0, 1.965, 206701, 1, 270, 206701, "レイ（ニューイヤー）のシャドウ", 0, 0, 400, "レイ（ニューイヤー）の姿をした特殊な魔物\n\n・敵全体に物理中ダメージを与え、\n　さらにスタンさせる。\n・【挑発】敵全体を挑発する。\n　挑発後のモーション中に攻撃を受けた場合、\n　範囲内の敵すべてに物理中ダメージを与え、\n　さらに物理防御力を小ダウンさせる。\n・自分の周囲の味方すべてに\n　魔法無効バリアを展開し、さらに\n　自身の魔法防御力を中アップさせる。");
INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 7, 0, 2.27, 206702, 2, 270, 206702, "ユイ（ニューイヤー）のシャドウ", 0, 0, 600, "ユイ（ニューイヤー）の姿をした特殊な魔物\n\n・味方全体に物理/魔法無効バリアと\n　継続ＨＰ回復状態を付与するが、\n　自身の魔法攻撃力を大ダウンさせる。\n・前から２番目の敵１キャラの\n　物理攻撃力と魔法攻撃力を大ダウンさせる。\n・魔法攻撃を行う全ての味方キャラの\n　魔法攻撃力を大アップさせる。");
