INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 0, 0, 3.5, 257500, 1, 270, 257500, "ウキキー", 0, 0, 900, "【物理】【遠距離】\n大きな岩を投げて、遠くから\n攻撃してくるサルの魔物。\n\n・一番後ろ攻撃\n・与えるダメージによるＴＰ回復量80％ダウン");
