INSERT INTO `v1_a9279319c7e496e97eeb7d347ef36b4624ea5d1450890bfef5280fec4c731642` VALUES (0, 13, 0, 3.5, 303100, 1, 270, 303100, "トウミングマ", 0, 0, 1000, "【物理】氷穴最奥に引き篭り\n生涯冬眠すると決めた巨大熊。\n長い歳月を夢うつつの中で過ごし\n獲物を狩るその一瞬、覚醒する。\n\n・敵全体に魔法大ダメージを与え、\n　物理攻撃力を下げる。\n\n・前方3キャラに物理小ダメージを\n　与える。さらに、前方1キャラを\n　大きく吹き飛ばし、氷結状態\n　にして移動速度を下げる。");
